import React, { Component } from "react";
import { connect } from "react-redux";
import L from "leaflet";
import {
  fetchAllBuildings,
  fetchBuildingAssets,
  setAssetDetail,
  fetchAssetDevicesCount

} from "../store/actions";
import filter from "../img/filter.svg";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";
import persianJs from "persianjs";
import { Link } from "react-router-dom";
import low from "../img/low.svg";
import high from "../img/high.svg";
import building from "../img/building.svg";

var waterIcon = L.icon({
  iconUrl: require("../img/water.svg"),
  iconRetinaUrl: require("../img/water.svg"),
  iconSize: [60, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});
var gasIcon = L.icon({
  iconUrl: require("../img/gas.svg"),
  iconRetinaUrl: require("../img/gas.svg"),
  iconSize: [60, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});
var electricityICon = L.icon({
  iconUrl: require("../img/elec.svg"),
  iconRetinaUrl: require("../img/elec.svg"),
  iconSize: [60, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      lng: 53.982,
      lat: 26.5425,
      zoom: 13,
      type: "electricity",
    };
  }
  _isMounted = false;

  componentDidMount() {
    this._isMounted = true;
    if (this._isMounted) {
      this.props.fetchAllBuildings();
      if (condition) {
        
      }
      this.props.fetchBuildingAssets(this.props.uuid);

      this.props.fetchAssetDevicesCount({ type: this.state.type });
    }
  }

  componentWillUnmount() {
    this._isMounted = false;
  }
  getIcon = () => {
    if (this.state.type === "electricity") {
      return electricityICon;
    }
    if (this.state.type === "water") {
      return waterIcon;
    }
    if (this.state.type === "gas") {
      return gasIcon;
    }
  };
  persionConvertor = (data) => {
    const persian = persianJs(`${data}`).englishNumber()._str;
    return persian;
  };
  renderMarker = () => {
    return this.props.assets.map((item, index) => {
      const position = [item.latitude, item.longitude];
      return (
        <Marker position={position} icon={this.getIcon()} key={index}>
          <Popup>
            <div className="pop-title">
              <span>{item.name}</span>
              <img src={building} width="30px" alt="" />
            </div>

            <div className="info-pop">
              <div className="d-flex justify-content-between">
                <span className="info-name">نام مالک:</span>
                <span className="info-num">{item.name}</span>
              </div>
              <div className="d-flex justify-content-between">
                <span className="info-name">نوع کنتور:</span>
                <span className="info-num">{this.getType()}</span>
              </div>
              <div className="d-flex justify-content-between">
                <span className="info-name">تعداد کنتور:</span>
                <span className="info-num">
                  {this.persionConvertor(this.props.DevicesCount)}

                </span>
              </div>

              <div className="d-flex justify-content-between">
                <span className="info-name">سیگنال:</span>
                <img
                  alt=""
                  className="info-num"
                  width="20px"
                  src={this.state.type  === "water" ? low : high}
                />
              </div>

              <Link
                onClick={() => this.setDetail(item)}
                className="btn pop-btn"
                to={`/Home/${item.name}`}
              >
                لیست کنتور ها
              </Link>
            </div>
          </Popup>
        </Marker>
      );
    });
  };
  getType = () => {
    const { type } = this.state;
    if (type === "electricity") {
      return "برق";
    }
    if (type === "water") {
      return "آب";
    }
    if (type === "gas") {
      return "گاز";
    }
  };
  setDetail = (item) => {
    this.props.setAssetDetail({
      assetId: item.uuid,
      type: this.state.type,
    });
  };
  onChangeValue = (event) => {
    this.setState({ type: event.target.value });
    this.props.fetchAssetDevicesCount({ type: this.state.type });

  };

  render() {
    const position = [this.state.lat, this.state.lng];
    return (
      <div className="col-lg-10 p-0">
        <div className="selector">
          <div className="title">
            <img src={filter} alt="" width="20px" />
            <span>فیلتر ها</span>
          </div>
          <div onChange={this.onChangeValue}>
            <label className="radio-box">
              <input
                className="mr-2"
                type="radio"
                value="electricity"
                name="type"
                defaultChecked
              />
              کنتور برق
            </label>
            <label className="radio-box">
              <input
                className="mr-2"
                type="radio"
                value="water"
                name="type"
              />
              کنتور آب
            </label>
            <label className="radio-box">
              <input
                className="mr-2"
                type="radio"
                value="gas"
                name="type"
              />
              کنتور گاز
            </label>
          </div>
        </div>
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </div>
    );
  }
}

const mapStateToProps = ({
  AllBuildingsReducer,
  BuildingAssetsReducer,
  AssetDevicesCountReducer

}) => {
  return {
    uuid: AllBuildingsReducer.data ,
    assets: BuildingAssetsReducer.data,
    DevicesCount: AssetDevicesCountReducer.data.length,
  };
};

export default connect(mapStateToProps, {
  fetchBuildingAssets,
  fetchAllBuildings,
  setAssetDetail,
  fetchAssetDevicesCount

})(Home);
