import React, { Component } from "react";
import { Switch, BrowserRouter, Route, Redirect} from "react-router-dom";
import Menu from "./Component/Menu";
import Home from "./Pages/Home";
import Gozareshat from "./Pages/Gozareshat";
import Setting from "./Pages/Setting";
import NotFound from "./Pages/NotFound";
import TowerDetail from "./Pages/TowerDetail";
import SearchResult from "./Pages/SearchResult";
import ElectricityTemplate from "./Pages/ElectricityTemplate";
import GetBill from "./Pages/GetBill";
import GasTemplate from "./Pages/GasTemplate";
import WaterTemplate from "./Pages/WaterTemplate";

class App extends Component {
  state={
    uuid:""
  }
  componentDidMount(){
    axios.get(`https://api.lora.atrovan.com/api/buildings/all?password=asd123`)
    .then(res => {
      const uuid = res.data.data[0].uuid;
      this.setState({ uuid });
    })
  }
  render() {
    return (
      <BrowserRouter>
        <section className="rtl container-fluid h-100">
          <div className="row h-100">
            <Menu />
            <Switch>
              <Route exact path="/">
                <Home uuid={this.state.uuid}/>
              </Route>
              <Route path="/Home/:id">
                <TowerDetail />
              </Route>

              <Route exact path="/Gozareshat">
                <Gozareshat />
              </Route>

              <Route path="/Gozareshat/search/:id">
                <SearchResult />
              </Route>

              <Route exact path="/Gozareshat/bill/">
                <GetBill />
              </Route>

              <Route path="/Gozareshat/bill/gas/:id">
                <GasTemplate />
              </Route>

              <Route path="/Gozareshat/bill/electricity/:id">
                <ElectricityTemplate />
              </Route>

              <Route path="/Gozareshat/bill/water/:id">
                <WaterTemplate />
              </Route>

              <Route path="/Setting">
                <Setting />
              </Route>

              <Route exact path="/Home">
                <Home />
              </Route>

              <Route path="*">
                <Redirect from="*" to="/404" />
                <NotFound />
              </Route>
            </Switch>
          </div>
        </section>
      </BrowserRouter>
    );
  }
}

export default App;
