import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Home from "./Home";
import Gozareshat from "./Gozareshat";
import Setting from "./Setting";

class Menu extends Component {
  state = {};
  render() {
    return (
        <div className="menu-tab mt-5">
        <ul className="rtl nav d-flex flex-column">
          <li
            className="nav-item"
            onClick={() => this.setState({ selectedTab: 1 })}
            style={{
              backgroundColor:
                this.state.selectedTab === 1 ? "white" : "none",
              borderRadius:
                this.state.selectedTab === 1 ? "0px 35px 35px 0px" : "none"
            }}
          >
            <Link
              className="nav-link active"
              to="/"
              style={{
                color: this.state.selectedTab === 1 ? "#11999e" : "white"
              }}
            >
              <img
                src={this.state.selectedTab === 1 ? file : filewithe}
                alt=""
              />
              لیست برنامه ها
            </Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="/exit">
              <img src={exit} alt="" />
              خروج
            </Link>
          </li>
        </ul>
      </div>
    );
  }
}

export default Menu;
