export const Taghvim = ({ getDate }) => {
  const [selectedDayRange, setSelectedDayRange] = useState({
    from: null,
    to: null
  });
  return (
    <Calendar 
    onClick={getDate}
      value={selectedDayRange}
      onChange={setSelectedDayRange}
      shouldHighlightWeekends
      locale="fa"
      calendarTodayClassName="custom-today-day" // also this
      colorPrimary="#bace24" // added this
      inputPlaceholder="Select a day range"
      colorPrimaryLight="rgb(196 216 46 / 26%)" // and this
    />
  );
};

const mapStateToProps = state => ({
  selectedDayRange: state.selectedDayRange
});

const mapDispatchToProps = { getDate };

export default connect(mapStateToProps, mapDispatchToProps)(Taghvim);
