import React, { Component } from "react";
import axios from "axios";
import L from "leaflet";
import {
  fetchDevicesID,
  fetchDevicesData,
  fetchAllBuildings,
  fetchBuildingAssets,
  fetchAssetDevices,
} from "../store/actions";
import { connect } from "react-redux";
import filter from "../img/filter.svg";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

var waterIcon = L.icon({
  iconUrl: require("../img/water.svg"),
  iconRetinaUrl: require("../img/water.svg"),
  iconSize: [60, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});
var gasIcon = L.icon({
  iconUrl: require("../img/gas.svg"),
  iconRetinaUrl: require("../img/gas.svg"),
  iconSize: [60, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});
var electricityICon = L.icon({
  iconUrl: require("../img/elec.svg"),
  iconRetinaUrl: require("../img/elec.svg"),
  iconSize: [60, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ID: [],
      info: [],
      lng: 53.982,
      lat: 26.5425,
      zoom: 13,
      type: "کنتور برق",
      towerCountElec: [],
      towerCountGas: [],
      towerCountWater: [],
    };
  }
  _isMounted = false;

  componentDidMount() {
    this._isMounted = true;
    if (this._isMounted) {
      this.props.fetchDevicesID();
      this.props.fetchAllBuildings();
      this.props.fetchBuildingAssets(this.props.uuid);

      for (let i = 0; i < this.props.assets.length; i++) {
        const id = this.props.assets[i].uuid;

        fetch(
          `https://api.lora.atrovan.com/api/devices/asset/${id}?limit=100&type=کنتور برق`,
          {
            method: "GET",
            redirect: "follow",
          }
        )
          .then((response) => response.json())
          .then((result) =>
            this.setState({
              towerCount: [...this.state.towerCountElec, result.data.length],
            })
          )
          .catch((error) => console.log("error", error));
      }
    }
  }

  componentWillUnmount() {
    this._isMounted = false;
  }
  getIcon = () => {
    if (this.state.type === "کنتور برق") {
      return electricityICon;
    }
    if (this.state.type === "کنتور آب") {
      return waterIcon;
    }
    if (this.state.type === "کنتور گاز") {
      return gasIcon;
    }
  };
  renderMarker = () => {
    return this.props.assets.map((item, index) => {
      const position = [item.latitude, item.longitude];
      if (this.state.type === "کنتور برق") {
        return sinaCount = this.state;
      }
      if (this.state.type === "کنتور آب") {
        return waterIcon;
      }
      if (this.state.type === "کنتور گاز") {
        return gasIcon;
      }
      return (
        <Marker position={position} icon={this.getIcon()} key={index}>
          <Popup>
            <span className="pop-title">اطلاعات دستگاه</span>

            <div className="info-pop">
              <div className="text-left">
                <span className="info-name">نام مالک:</span>
                <span className="info-num">{item.name}</span>
              </div>
              <div className="text-left">
                <span className="info-name">نوع کنتور:</span>
                <span className="info-num">{item.country}</span>
              </div>
              <div className="text-left">
                <span className="info-name">تعداد کنتور:</span>
                <span className="info-num">
                  {item.name === "برج سینا"
                    ? this.state.towerCount[0]
                    : this.state.towerCount[1]}
                </span>
              </div>
              <div className="text-left">
                <span className="info-name">شهر:</span>
                <span className="info-num">{item.country}</span>
              </div>
              <div className="text-left">
                <span className="info-name">آدرس:</span>
                <span className="info-num">{item.address}</span>
              </div>
            </div>
          </Popup>
        </Marker>
      );
    });
  };
  onChangeValue = (event) => {
    this.setState({ type: event.target.value });

    this.setState({
      towerCountGas: [],
      towerCountElec: [],
      towerCountWater: [],
    });

    for (let i = 0; i < this.props.assets.length; i++) {
      const id = this.props.assets[i].uuid;

      fetch(
        `https://api.lora.atrovan.com/api/devices/asset/${id}?limit=100&type=${event.target.value}`,
        {
          method: "GET",
          redirect: "follow",
        }
      )
        .then((response) => response.json())
        .then((result) => {
          this.setState({
            towerCount: [...this.state.towerCountWater, result.data.length],
          });
        })
        .catch((error) => console.log("error", error));
    }
  };
  render() {
    console.log(this.state.towerCount);
    const position = [this.state.lat, this.state.lng];
    return (
      <div className="col-lg-10 p-0">
        <div className="selector">
          <div className="title">
            <img src={filter} alt="" width="20px" />
            <span>فیلتر ها</span>
          </div>
          <div onChange={this.onChangeValue}>
            <label className="radio-box">
              <input
                className="mr-2"
                type="radio"
                value="کنتور برق"
                name="type"
                defaultChecked
              />
              کنتور برق
            </label>
            <label className="radio-box">
              <input
                className="mr-2"
                type="radio"
                value="کنتور آب"
                name="type"
              />
              کنتور آب
            </label>
            <label className="radio-box">
              <input
                className="mr-2"
                type="radio"
                value="کنتور گاز"
                name="type"
              />
              کنتور گاز
            </label>
          </div>
        </div>
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </div>
    );
  }
}

const mapStateToProps = ({
  DevicesIDReducer,
  DevicesDataReducer,
  AllBuildingsReducer,
  BuildingAssetsReducer,
  AssetDevicesReducer,
}) => {
  return {
    id: DevicesIDReducer.data,
    uuid: AllBuildingsReducer.data[0].uuid,
    assets: BuildingAssetsReducer.data,
    // devices: AssetDevicesReducer.data,
  };
};

export default connect(mapStateToProps, {
  fetchDevicesID,
  fetchBuildingAssets,
  fetchAllBuildings,
  // fetchAssetDevices,
})(Home);
