const INITIAL_STATE = {
  data: [],
};

export default function DevicesDataReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case "FETCH_DEVICES_DATA":
      return { data: [...state.data, action.payload] };

    default:
      return state;
  }
}
