import React, { Component } from "react";
import waterBillLogo from "../img/Ab-o-Fazelab-01.svg";
import Taghvim from "../Component/Taghvim";
import { setItem, setDate, getBill } from "../store/actions";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

import moment from "jalali-moment";
import UserIcon from "../Component/UserIcon";
import CodeIcon from "../Component/CodeIcon";
import TagIcon from "../Component/TagIcon";
import HomeIcon from "../Component/HomeIcon";

class WaterBill extends Component {



  renderBtn = () => {
    if (this.props.dateRange.dateRange) {
      const { dateRange } = this.props.dateRange;
      if ((dateRange.from, dateRange.to)) {
        return (
          <Link
            className="btn calendar-btn"
            onClick={() => this.getBill(dateRange.from, dateRange.to)}
            to={`/Gozareshat/water/result`}
          >
            نمایش قبض
          </Link>
        );
      } else {
        return <a className="btn calendar-btn deactive"> نمایش قبض</a>;
      }
    } else {
      return <a className="btn calendar-btn deactive"> نمایش قبض</a>;
    }
  };
  getBill = (from, to) => {
    const beginTime =
      new Date(
        moment
          .from(`${from.year}.${from.month}.${from.day}`, "fa", "YYYY/MM/DD")
          .format("YYYY/MM/DD")
      ).getTime() / 1000;

    const endTime =
      new Date(
        moment
          .from(`${to.year}.${to.month}.${to.day}`, "fa", "YYYY/MM/DD")
          .format("YYYY/MM/DD")
      ).getTime() / 1000;

    this.props.getBill({
      deviceID: this.props.newItem.uuid,
      beginTime,
      endTime,
    });
  };
  render() {
    console.log(this.props.billData);
    return (
      <div className=" col-lg-10">
        <div className="bill info-pop">
          <div className="date-range-title">
            
            ابتدا و انتهای بازه زمانی را انتخاب کنید.
          </div>
          <div className="text-left mb-5">
            <img src={waterBillLogo} width="70px" alt="" />
          </div>
          <div className="text-left">
            <UserIcon />
            <span className="info-name">نام مالک:</span>
            <span className="info-num">{this.props.newItem.owner}</span>
          </div>
          <div className="text-left">
            <CodeIcon />

            <span className="info-name">شماره شناسه:</span>
            <span className="info-num">{this.props.newItem.zipCode}</span>
          </div>
          <div className="text-left">
            <HomeIcon />

            <span className="info-name">نوع کنتور:</span>
            <span className="info-num">{this.props.newItem.subtype}</span>
          </div>
          <div className="text-left">
            <TagIcon />

            <span className="info-name">برند:</span>
            <span className="info-num">{this.props.newItem.brand}</span>
          </div>

          {this.renderBtn()}
          <Taghvim setDate={this.props.setDate} />
        </div>

      </div>
    );
  }
}

const mapStateToProps = ({ SetItemReducer, SetDateReducer, GetBillReducer }) => {
  return {
    newItem: SetItemReducer.data.item,
    dateRange: SetDateReducer.data,
    billData: GetBillReducer,
  };
};
export default connect(mapStateToProps, {
  setItem,
  setDate,
  getBill
})(WaterBill);
