import React, { Component } from "react";
import L from "leaflet";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

var mapIcon = L.icon({
  iconUrl: require("../img/placeholder.svg"),
  iconRetinaUrl: require("../img/placeholder.svg"),
  iconSize: [50, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  state = {
    deviceData: [],
    ID: [],
    // info: [],
    locations: [],
    lng: 53.9868,
    lat: 26.5325,
    zoom: 13,
  };

  componentDidMount() {
    var idRequestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/devices/id",
      idRequestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        const ID = result.devicesID;
        console.log(result);
        for (let i = 0; i < ID.length; i++) {
          const element = ID[i];

        }
      })
      .catch((error) => console.log("error", error));
  }

  renderMarker = () => {
    return this.state.locations.map((item) => {
      const position = [item.latitude, item.longitude];
      return (
        <Marker position={position} icon={mapIcon} key={item.lat}>
          <Popup>
            A pretty CSS3 popup. <br /> Easily customizable.
          </Popup>
        </Marker>
      );
    });
  };
  render() {
    const position = [this.state.lat, this.state.lng];
    return (
      <section>
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </section>
    );
  }
}

export default Home;
