import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter, Link } from "react-router-dom";
import {
  fetchAssetDevices,
  setAssetDetail,
  fetchSingleAsset,
} from "../store/actions";
import building from "../img/building.svg";
import low from "../img/low.svg";
import high from "../img/high.svg";
import BackIcon from "../Component/BackIcon";
import UserIcon from "../Component/UserIcon";
import CodeIcon from "../Component/CodeIcon";
import TagIcon from "../Component/TagIcon";
import HomeIcon from "../Component/HomeIcon";
import waterIcon from "../img/water-icon.svg";
import gasIcon from "../img/gas-icon.svg";
import electricityICon from "../img/elec-icon.svg";

class TowerDetail extends Component {
  componentDidMount() {
    this.props.fetchSingleAsset({
      assetId: this.props.id,
      type: this.props.type,
    });
    this.props.fetchAssetDevices({
      assetId: this.props.id,
      type: this.props.type,
    });
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  goBack = () => {
    this.props.history.push("/");
  };
  renderTower = () => {
    const { assetInfo, devices, type } = this.props;

    return (
      <div className="card tower-info">
        <div className="card-body">
          <div className="w-50">
            <div className="tower-name">
              <img src={building} width="50px" alt="" />
              <h3 className="card-title">{assetInfo.name}</h3>
            </div>
            <div className="d-flex justify-content-between">
              <div>
                <p className="card-text">
                  نوع:
                  <span>{assetInfo.type}</span>
                </p>
                <p className="card-text">
                  تعداد کنتور :<span>{devices.length}</span>
                </p>
                <p className="card-text">
                  سیگنال :
                  <img
                    alt=""
                    className="ml-2"
                    width="20px"
                    src={assetInfo.name === "برج سینا" ? low : high}
                  />
                </p>
              </div>
              <div>
                <p className="card-text">
                  نوع مالک:
                  <span>{assetInfo.subtype}</span>
                </p>
                <p className="card-text">
                  آدرس :<span>{assetInfo.address}</span>
                </p>
                <p className="card-text">
                  توضیحات :<span>{assetInfo.description}</span>
                </p>
              </div>

              <div className="tag-box">
                <div className="tag">
                  <img src={this.getIcon()} width="30px" alt="" />
                  {type}
                </div>
                <div className="tag">{assetInfo.city}</div>
                <div className="tag">{assetInfo.type}</div>
              </div>
            </div>
          </div>
          <a onClick={this.goBack} className="btn btn-light mt-auto">
            <BackIcon />
            بازگشت
          </a>
        </div>
      </div>
    );
  };
  getIcon = () => {
    const { type } = this.props;
    if (type === "کنتور برق") {
      return electricityICon;
    }
    if (type === "کنتور آب") {
      return waterIcon;
    }
    if (type === "کنتور گاز") {
      return gasIcon;
    }
  };
  renderList = () => {
    const { devices } = this.props;
    return devices.map((item, index) => {
      return (
        <div key={index} className="list-item colg4">
          <div className="info-pop">
            <div className="text-left">
              <UserIcon />
              <span className="info-name">نام مالک:</span>
              <span className="info-num">{item.owner}</span>
            </div>
            <div className="text-left">
              <CodeIcon />

              <span className="info-name">شماره شناسه:</span>
              <span className="info-num">{item.zipCode}</span>
            </div>
            <div className="text-left">
              <HomeIcon />

              <span className="info-name">نوع کنتور:</span>
              <span className="info-num">{item.subtype}</span>
            </div>
            <div className="text-left">
              <TagIcon />

              <span className="info-name">برند:</span>
              <span className="info-num">{item.brand}</span>
            </div>
          </div>

          <Link className="btn " to={`/Gozareshat/${item.applicationID}`}>
            مشاهده جزییات
          </Link>
        </div>
      );
    });
  };
  render() {
    console.log(this.props);
    return (
      <div className="col-lg-10">
        <div>{this.renderTower()}</div>
        <div className="list-box">{this.renderList()}</div>
      </div>
    );
  }
}

const mapStateToProps = ({
  AssetDevicesReducer,
  SingleAssetReducer,
  AssetDetailReducer,
}) => {
  return {
    devices: AssetDevicesReducer.data,
    // assetInfo: AssetDetailReducer.data.asset,
    assetInfo: SingleAssetReducer.data,
    id: AssetDetailReducer.data.assetId,
    type: AssetDetailReducer.data.type,
  };
};

export default connect(mapStateToProps, {
  fetchAssetDevices,
  setAssetDetail,
  fetchSingleAsset,
})(withRouter(TowerDetail));
