import React, { Component } from "react";
import { connect } from "react-redux";
import {  Link } from "react-router-dom";
import {
set

} from "../store/actions";
import UserIcon from "../Component/UserIcon";
import CodeIcon from "../Component/CodeIcon";
import TagIcon from "../Component/TagIcon";
import HomeIcon from "../Component/HomeIcon";
class DevicesItem extends Component {
  state = {};
  getLink = (item) =>{
    this.props.setItem(item)
    if (item.type === "electricity") {
      return `/Gozareshat/electricity/${item.applicationID}`;
    }
    if (item.type === "water") {
      return `/Gozareshat/water/${item.applicationID}`;

    }
    if (item.type === "gas") {
      return `/Gozareshat/gas/${item.applicationID}`;
    }
    
  }
  render() {
    const { item } = this.props;
    // console.log(item);
    return (
      <div  className="list-item">
        <div className="info-pop">
          <div className="text-left">
            <UserIcon />
            <span className="info-name">نام مالک:</span>
            <span className="info-num">{item.owner}</span>
          </div>
          <div className="text-left">
            <CodeIcon />

            <span className="info-name">شماره شناسه:</span>
            <span className="info-num">{item.zipCode}</span>
          </div>
          <div className="text-left">
            <HomeIcon />

            <span className="info-name">نوع کنتور:</span>
            <span className="info-num">{item.subtype}</span>
          </div>
          <div className="text-left">
            <TagIcon />

            <span className="info-name">برند:</span>
            <span className="info-num">{item.brand}</span>
          </div>
        </div>

        <Link className="btn " to={this.getLink(item)}>
          مشاهده جزییات
        </Link>
      </div>
    );
  }
}

export default DevicesItem;
