import React, { Component } from "react";
import { connect } from "react-redux";
import { getBill } from "../store/actions";
import waterBillLogo from "../img/Ab-o-Fazelab-01.svg";

class WaterTemplate extends Component {
  state = {};
  render() {
    const { billData } = this.props;
    return (
      <div className=" col-lg-10 pt-3 pb-3">
        <div className="bill-template">
          <div>
            <img src={waterBillLogo} width="50px" alt="" />
            شرکت آب و فاضلاب
          </div>
          <div>
            <div className="d-flex justify-content-between water-border">
              <div className="bill-header">
                <div>
                  <span>مشترک گرامی:</span> <p>{billData.owner}</p>
                </div>
                <div>
                  <span>شماره اشتراک:</span> <p>{billData.consumer_id}</p>
                </div>
                <div>
                  <span className="red">شماره پرونده:</span>{" "}
                  <p>{billData.file_number}</p>
                </div>
              </div>
              <div className="bill-header">
                <div>
                  <span>کد و نوع تعرفه:</span> <p>مختلط</p>
                </div>
                <div>
                  <span>کدپستی:</span> <p></p>
                </div>
                <div>
                  <span>نشانی:</span> <p></p>
                </div>
              </div>
              <div className="bill-header">
                <div>
                  <span>کد مامور قرائت:</span> <p></p>
                </div>
                <div>
                  <span>محدوده قرائت:</span> <p></p>
                </div>
              </div>
              <div className="bill-header">
                <div>
                  <span>تاریخ صدور قبض:</span> <p></p>
                </div>
                <div>
                  <span>مراجعه بعدی:</span> <p></p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = ({ GetBillReducer }) => {
  return {
    billData: GetBillReducer.data,
  };
};
export default connect(mapStateToProps, {
  getBill,
})(WaterTemplate);
