const INITIAL_STATE = {
    data:[]
}

export default function DevicesDataReducer(state = {}, action) {
  switch (action.type) {
    case "FETCH_DEVICES_DATA":
      // console.log("FETCH_DEVICES_DATA")
      // console.log(action.payload.options)
      // console.log(state);
      state= {data:[]}
      return {  data: [...state.data, action.payload] }

    default:
      return state;
  }
}
