import React, { useEffect, useState }  from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import persianJs from "persianjs";
import {
  fetchAssetDevices,
  setAssetDetail,
  fetchSingleAsset,
} from "../store/actions";
import DevicesItem from "../Component/DevicesItem";
import building from "../asset/img/building.svg";
import low from "../asset/img/low.svg";
import high from "../asset/img/high.svg";
import BackIcon from "../asset/icons/BackIcon";
import waterIcon from "../asset/img/water-icon.svg";
import gasIcon from "../asset/img/gas-icon.svg";
import electricityICon from "../asset/img/elec-icon.svg";

const TowerDetail = () => {
  const dispatch = useDispatch();
  useEffect(()=>{
    dispatch(fetchSingleAsset({
      assetId: this.props.id,
      type: this.props.type,
    }));
    dispatch(fetchAssetDevices({
      assetId: this.props.id,
      type: this.props.type,
    }));
  },[])
  // componentDidMount() {
  //   this.props.fetchSingleAsset({
  //     assetId: this.props.id,
  //     type: this.props.type,
  //   });
  //   this.props.fetchAssetDevices({
  //     assetId: this.props.id,
  //     type: this.props.type,
  //   });
  // }
  // componentWillUnmount() {
  //   this._isMounted = false;
  // }
  console.log([props]);
  const goBack = () => {
    this.props.history.push("/");
  };
  const persionConvertor = (data) => {
    const persian = persianJs(`${data}`).englishNumber()._str;
    return persian;
  };
  const renderTower = () => {
    const { assetInfo, devices } = this.props;

    return (
      <div className="card tower-info">
        <div className="card-body">
          <div className="w-50">
            <div className="tower-name">
              <img src={building} width="50px" alt="" />
              <h3 className="card-title">{assetInfo.name}</h3>
            </div>
            <div className="d-flex justify-content-between">
              <div>
                <p className="card-text">
                  نوع:
                  <span>{assetInfo.type}</span>
                </p>
                <p className="card-text">
                  تعداد کنتور :
                  <span>{this.persionConvertor(devices.length)}</span>
                </p>
                <p className="card-text">
                  سیگنال :
                  <img
                    alt=""
                    className="ml-2"
                    width="20px"
                    src={assetInfo.name === "برج سینا" ? low : high}
                  />
                </p>
              </div>
              <div>
                <p className="card-text">
                  نوع مالک:
                  <span>{assetInfo.subtype}</span>
                </p>
                <p className="card-text">
                  آدرس :<span>{assetInfo.address}</span>
                </p>
                <p className="card-text">
                  توضیحات :<span>{assetInfo.description}</span>
                </p>
              </div>

              <div className="tag-box">
                <div className="tag">
                  <img src={this.getIcon()} width="30px" alt="" />
                  {this.getType()}
                </div>
                <div className="tag">{assetInfo.city}</div>
                <div className="tag">{assetInfo.type}</div>
              </div>
            </div>
          </div>
          <a onClick={this.goBack} className="btn btn-light mb-auto">
            <BackIcon />
            بازگشت
          </a>
        </div>
      </div>
    );
  };
  const getIcon = () => {
    const { type } = this.props;
    if (type === "electricity") {
      return electricityICon;
    }
    if (type === "water") {
      return waterIcon;
    }
    if (type === "gas") {
      return gasIcon;
    }
  };
  const getType = () => {
    const { type } = this.props;
    if (type === "electricity") {
      return "برق";
    }
    if (type === "water") {
      return "آب";
    }
    if (type === "gas") {
      return "گاز";
    }
  };
  const renderList = () => {
    const { devices } = this.props;
    return devices.map((item, index) => {
      return <DevicesItem key={index} item={item} />;
    });
  };
    return (
      <div className="content-width">
        <div>{this.renderTower()}</div>
        <div className="list-box">{this.renderList()}</div>
      </div>
    );
}

const mapStateToProps = ({
  AssetDevicesReducer,
  SingleAssetReducer,
  AssetDetailReducer,
}) => {
  return {
    devices: AssetDevicesReducer.data,
    assetInfo: SingleAssetReducer.data,
    id: AssetDetailReducer.data.assetId,
    type: AssetDetailReducer.data.type,
  };
};

export default (withRouter(TowerDetail));

// export default connect(mapStateToProps, {
//   fetchAssetDevices,
//   setAssetDetail,
//   fetchSingleAsset,
// })(withRouter(TowerDetail));
