import axios from "axios";

export const fetchDevicesID = () => async dispatch => {
    const response = await axios.get(
      "https://api.lora.atrovan.com/api/telemetry/devices/id"
    );
    console.log(response.data);
    dispatch({
      type: "FETCH_DEVICES_ID",
      payload: response.data
    });


    const ID = response.data.devicesID;
    fetchDevicesData(ID)
    console.log(ID);

      for (let i = 0; i < ID.length; i++) {
        const elementID = ID[i];
        fetch(
          `https://api.lora.atrovan.com/api/telemetry/device/latest/${elementID}`,
          RequestOptions
        )
          .then((response) => response.json())
          .then((result) => {
            if (this._isMounted) {
              if (result.options.object) {
              console.log(result);
              this.setState({
                info: [...this.state.info, result.options],
              });
            }
            // console.log(this.state.info);
            }
          })
          .catch((error) => console.log("error", error));
      }
  };
  

