import React, { Component } from "react";
import { withRouter, Link } from "react-router-dom";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import {
  fetchAllBuildings,
  fetchBuildingDevices,
  searchBuildingDevices,
} from "../store/actions";

import DevicesItem from "../Component/DevicesItem";
import SearchIcon from "../Component/SearchIcon";

class Gozareshat extends Component {
  _isMounted = false;
  state = {
    ID: [],
    info: [],
    NotFound: null,
    searchText: "",
  };

  componentDidMount() {
    this._isMounted = true;
    if (this._isMounted) {
      this.props.fetchBuildingDevices(this.props.uuid);
    }
    this.validate()
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  validate=()=>{
    document.addEventListener("DOMContentLoaded", function() {  
      var elements = document.getElementsByTagName("INPUT");
      for (var i = 0; i < elements.length; i++) {
          elements[i].oninvalid = function(e) {
              e.target.setCustomValidity("");
              if (!e.target.validity.valid) {
                  e.target.setCustomValidity("لطفا این بخش را پر کنید");
              }
          };
          elements[i].oninput = function(e) {
              e.target.setCustomValidity("");
          };
      }
  })
  }
  handleChange = (event) => {
    this.setState({ searchText: event.target.value });
  };
  handleSubmit = (event) => {
    event.preventDefault();
    this.props.searchBuildingDevices({
      buildingId: this.props.uuid,
      searchText: this.state.searchText,
    });
      
      let path = `/results/${this.state.searchText}`;
      this.props.history.push(path)
    document.getElementById("mainInput").reset();
  };

  renderList = () => {
    const { buildingDevices } = this.props;
    return buildingDevices.map((item, index) => {
      return (
        <DevicesItem index={index} item={item}/>
      );
    });
  };
  renderSearchResult=()=>{
    return <div>hello</div>
  }

  render() {
    console.log(this.props);
    return (
      <div className="gozareshat-bg p-3 col-lg-10">
        <div className="w-100 form-box">
          <h6>برای جستجوی کنتور مورد نظر شناسه کنتور را وارد کنید.</h6>
          <form
            onSubmit={this.handleSubmit}
            id="mainInput"
            className="form-inline my-2 my-lg-0 search-form"
          >
            <input
              className="form-control mr-sm-2 "
              value={this.state.searchText}
              onChange={this.handleChange}
              type="number"
              placeholder="شماره شناسه کنتور..."
              aria-label="Search"
              required
            />
            {/* <Link type="submit" className="btn btn-outline-success  " to={this.props.searchResult.length>0?`/result/${this.state.searchText}`: "/Gozareshat"}>
            جستجو
              <SearchIcon />
            </Link> */}
            <button
              className="btn btn-outline-success my-2 my-sm-0"
              type="submit"
            >
              جستجو
              <SearchIcon />
            </button>
          </form>
        </div>
        <div className="list-box" id="style-7">
          {this.props.searchResult.length!==0
            ? this.renderSearchResult()
            : this.renderList()}
        </div>
      </div>
      
    );
  }
}
const mapStateToProps = ({
  AllBuildingsReducer,
  BuildingDevicesReducer,
  SearchBuildingReducer,
}) => {
  return {
    uuid: AllBuildingsReducer.data[0].uuid,
    buildingDevices: BuildingDevicesReducer.data.data,
    searchResult: SearchBuildingReducer.data,
  };
};
export default connect(mapStateToProps, {
  fetchAllBuildings,
  fetchBuildingDevices,
  searchBuildingDevices,
})(withRouter(Gozareshat));
