import React, { Component } from "react";
class Gozareshat extends Component {
  _isMounted = false;
  state = {
    ID: [],
    info: [],
  };

  componentDidMount() {
    this._isMounted = true;
    var RequestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/devices/id",
      RequestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        const ID = result.devicesID;
        for (let i = 0; i < ID.length; i++) {
          const element = ID[i];
          if (this._isMounted) {
            this.setState({ ID: [...this.state.ID, element] });
          }
        }
        for (let i = 0; i < this.state.ID.length; i++) {
          const element = this.state.ID[i];
          fetch(
            `https://api.lora.atrovan.com/api/telemetry/device/latest/${element}`,
            RequestOptions
          )
            .then((response) => response.json())
            .then((result) => {
              if (this._isMounted) {
                console.log(result);
                this.setState({
                  info: [...this.state.info, result.options],
                });
              }
              // console.log(result.options.rxInfo[0]);
            })
            .catch((error) => console.log("error", error));
        }
      })
      .catch((error) => console.log("error", error));
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  renderList = ()=>{
    return this.state.info.map((item, index) => {
      if (item.object.input2.length >= 20) {
        const hex2ascii = require("hex2ascii");
        const hexPaketID = item.object.input2.slice(2, 34);
        const packetID = hex2ascii(hexPaketID);

        const hexVolt = item.object.input2.slice(150, 164);
        const volt = hex2ascii(hexVolt);

        const hexAmper = item.object.input2.slice(136, 150);
        const amper = hex2ascii(hexAmper);

        const hexP180 = item.object.input2.slice(36, 56);
        const p180 = hex2ascii(hexP180);

        const hexP181 = item.object.input2.slice(56, 76);
        const p181 = hex2ascii(hexP181);

        const hexP182 = item.object.input2.slice(76, 96);
        const p182 = hex2ascii(hexP182);

        const hexP183 = item.object.input2.slice(96, 116);
        const p183 = hex2ascii(hexP183);

        const hexP184 = item.object.input2.slice(116, 136);
        const p184 = hex2ascii(hexP184);
        return(
          <div key={index}></div>
        )
      }
    })
  }
  render() {
    console.log(this.state);
    return (
      <section className="gozareshat-bg p-3">
        <div className="w-100 form-box">
          <h6>برای جستجوی کنتور مورد نظر شناسه کنتور را وارد کنید.</h6>
          <form className="form-inline my-2 my-lg-0 search-form">
            <input
              className="form-control mr-sm-2 "
              type="search"
              placeholder="شماره شناسه کنتور..."
              aria-label="Search"
            />
            <button
              className="btn btn-outline-success my-2 my-sm-0"
              type="submit"
            >
              جستجو
            </button>
          </form>
        </div>
        <div>{this.renderList()}</div>
      </section>
    );
  }
}

export default Gozareshat;
