import React, { Component } from "react";
import notfound from "../img/page-not-found.svg";
class SearchResult extends Component {
  state = {};
  renderList = () => {
    return this.props.searchResult.map((item, index) => {
      if (item.object.input2.length >= 20) {
        const hex2ascii = require("hex2ascii");

        const hexPaketID = item.object.input2.slice(2, 34);
        const packetID = hex2ascii(hexPaketID);

        const hexVolt = item.object.input2.slice(150, 164);
        const volt = hex2ascii(hexVolt);

        const hexAmper = item.object.input2.slice(136, 150);
        const amper = hex2ascii(hexAmper);
        return (
          <div key={index} className="list-item">
            <div className="info-pop">
              <div className="text-left">
                <span className="info-name">نام دستگاه:</span>
                <span className="info-num">{item.rxInfo[0].name}</span>
              </div>
              <div className="text-left">
                <span className="info-name">شماره شناسه:</span>
                <span className="info-num">{packetID}</span>
              </div>
              <div className="text-left">
                <span className="info-name">ولتاژ:</span>
                <span className="info-num">{volt}</span>
              </div>
              <div className="text-left">
                <span className="info-name">آمپر:</span>
                <span className="info-num">{amper}</span>
              </div>
            </div>
            <button className="btn ">مشاهده جزییات</button>
          </div>
        );
      }
    });
  };
  render() {
    console.log(this.props.NotFound);
    if (this.props.NotFound === "موردی یافت نشد!") {
      return (
        <div className="mt-3 result-notfound align-items-center d-flex flex-column h-75 justify-content-center">
          <img src={notfound} width="10%" alt="" />
          <span>موردی یافت نشد!</span>
        </div>
      );
    } else {
      return (
        <div className="list-box mt-3">
          <div class="alert alert-light w-100" >
            A simple light alert with{" "}
            <a href="#" class="alert-link">
              an example link
            </a>
            . Give it a click if you like.
          </div>
          <div>موارد یافت شده برای {this.props.filter}</div>
          {this.renderList()}
        </div>
      );
    }
  }
}

export default SearchResult;
