import React, { Component } from "react";
import { withRouter, Link } from "react-router-dom";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import {
  fetchAllBuildings,
  fetchBuildingDevices,
  searchBuildingDevices,
} from "../store/actions";
import UserIcon from "../Component/UserIcon";
import CodeIcon from "../Component/CodeIcon";
import TagIcon from "../Component/TagIcon";
import HomeIcon from "../Component/HomeIcon";
import SearchIcon from "../Component/SearchIcon";

class Gozareshat extends Component {
  _isMounted = false;
  state = {
    ID: [],
    info: [],
    NotFound: null,
    searchText: "",
  };

  componentDidMount() {
    this._isMounted = true;
    if (this._isMounted) {
      this.props.fetchBuildingDevices(this.props.uuid);
    }
    (function() {
      'use strict';
      window.addEventListener('load', function() {
      // Fetch all the forms we want to apply custom Bootstrap validation styles to
      var forms = document.getElementsByClassName('needs-validation');
      // Loop over them and prevent submission
      var validation = Array.prototype.filter.call(forms, function(form) {
      form.addEventListener('submit', function(event) {
      if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
      }
      form.classList.add('was-validated');
      }, false);
      });
      }, false);
      })();
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  handleChange = (event) => {
    this.setState({ searchText: event.target.value });
  };
  handleSubmit = (event) => {
    event.preventDefault();
    this.props.searchBuildingDevices({
      buildingId: this.props.uuid,
      searchText: this.state.searchText,
    });

    document.getElementById("mainInput").reset();
  };

  renderList = () => {
    const { buildingDevices } = this.props;
    return buildingDevices.map((item, index) => {
      return (
        <div key={index} className="list-item">
          <div className="info-pop">
            <div className="text-left">
              <UserIcon />
              <span className="info-name">نام مالک:</span>
              <span className="info-num">{item.owner}</span>
            </div>
            <div className="text-left">
              <CodeIcon />

              <span className="info-name">شماره شناسه:</span>
              <span className="info-num">{item.zipCode}</span>
            </div>
            <div className="text-left">
              <HomeIcon />

              <span className="info-name">نوع کنتور:</span>
              <span className="info-num">{item.subtype}</span>
            </div>
            <div className="text-left">
              <TagIcon />

              <span className="info-name">برند:</span>
              <span className="info-num">{item.brand}</span>
            </div>
          </div>

          <Link className="btn " to={`/Gozareshat/${item.applicationID}`}>
            مشاهده جزییات
          </Link>
        </div>
      );
    });
  };
  renderSearchResult=()=>{
    return <div>hello</div>
  }

  render() {
    console.log(this.props.searchResult);
    return (
      // <div className="gozareshat-bg p-3 col-lg-10">
      //   <div className="w-100 form-box">
      //     <h6>برای جستجوی کنتور مورد نظر شناسه کنتور را وارد کنید.</h6>
      //     <form
      //       onSubmit={this.handleSubmit}
      //       id="mainInput"
      //       className="form-inline my-2 my-lg-0 search-form"
      //     >
      //       <input
      //         className="form-control mr-sm-2 "
      //         value={this.state.searchText}
      //         onChange={this.handleChange}
      //         type="number"
      //         placeholder="شماره شناسه کنتور..."
      //         aria-label="Search"
      //         required
      //       />
      //       {/* <Link type="submit" className="btn btn-outline-success  " to={this.props.searchResult.length>0?`/result/${this.state.searchText}`: "/Gozareshat"}>
      //       جستجو
      //         <SearchIcon />
      //       </Link> */}
      //       <button
      //         className="btn btn-outline-success my-2 my-sm-0"
      //         type="submit"
      //       >
      //         جستجو
      //         <SearchIcon />
      //       </button>
      //     </form>
      //   </div>
      //   <div className="list-box" id="style-7">
      //     {this.props.searchResult.length!==0
      //       ? this.renderSearchResult()
      //       : this.renderList()}
      //   </div>
      // </div>
      <form class="needs-validation" noValidate>
  <div class="form-row">
    <div class="col-md-4 mb-3 md-form">
      <label for="validationCustom012">First name</label>
      <input type="text" class="form-control" id="validationCustom012" placeholder="First name" value="Mark"
        required/>
      <div class="valid-feedback">
        Looks good!
      </div>
    </div>
    <div class="col-md-4 mb-3 md-form">
      <label for="validationCustom022">Last name</label>
      <input type="text" class="form-control" id="validationCustom022" value="Otto" required/>
      <div class="valid-feedback">
        Looks good!
      </div>
    </div>
    <div class="col-md-4 mb-3 md-form">
      <label for="validationCustomUsername2">Username</label>
      <input type="text" class="form-control" id="validationCustomUsername2" aria-describedby="inputGroupPrepend2"
        required/>
      <div class="invalid-feedback">
        Please choose a username.
      </div>
    </div>
  </div>
  <div class="form-row">
    <div class="col-md-6 mb-3 md-form">
      <label for="validationCustom032">City</label>
      <input type="text" class="form-control" id="validationCustom032" required/>
      <div class="invalid-feedback">
        Please provide a valid city.
      </div>
    </div>
    <div class="col-md-3 mb-3 md-form">
      <label for="validationCustom042">State</label>
      <input type="text" class="form-control" id="validationCustom042" required/>
      <div class="invalid-feedback">
        Please provide a valid state.
      </div>
    </div>
    <div class="col-md-3 mb-3 md-form">
      <label for="validationCustom052">Zip</label>
      <input type="text" class="form-control" id="validationCustom052" required/>
      <div class="invalid-feedback">
        Please provide a valid zip.
      </div>
    </div>
  </div>
  <div class="form-group">
    <div class="form-check pl-0">
      <input class="form-check-input" type="checkbox" value="" id="invalidCheck2" required/>
      <label class="form-check-label" for="invalidCheck2">
        Agree to terms and conditions
      </label>
      <div class="invalid-feedback">
        You must agree before submitting.
      </div>
    </div>
  </div>
  <button class="btn btn-primary btn-sm btn-rounded" type="submit">Submit form</button>
</form>
    );
