import React, { Component } from "react";
import { connect } from "react-redux";
import { getBill } from "../store/actions";
import persianJs from "persianjs";

import moment from "jalali-moment";
import ElecLogo from "../img/Tavanir_logo-01.svg";

class ElectricityTemplate extends Component {
  timeConverter = (time) => {
    const engDate = moment(time * 1000).format("YYYY/MM/DD");
    const perSianDate = persianJs(
      moment(`${engDate}`, "YYYY/MM/DD").locale("fa").format("YYYY/MM/DD")
    ).englishNumber()._str;
    return perSianDate;
  };
  persionConvertor = (data) => {
    const persian = persianJs(`${data}`).englishNumber()._str;
    return persian;
  };
  translateNumber = (num) => {
    const translatedNum = persianJs(num).digitsToWords().toString();
    return translatedNum;
  };
  render() {
    const { billData } = this.props;
    return (
      <div className=" col-lg-10 pt-3 pb-3">
        <div className="bill-template elec-template ">
          <img src={ElecLogo} width="40px" alt="" />
          <img src={ElecLogo} width="40px" alt="" />
          <div className="col-4">
            <div className="mt-2 border-bottom">
              <div className="d-flex">
                <span>مشترک محترم:</span>
                <p className="ml-2">{billData.owner}</p>
              </div>
              <div className="d-flex">
                <span>نشانی:</span>
                <p className="ml-2">{billData.address}</p>
              </div>
              <div className="d-flex justify-content-between">
                <div className="d-flex">
                  <span>کدپستی:</span>
                  <p className="ml-2">{this.persionConvertor(billData.zipCode)}</p>
                </div>
                <div className="d-flex">
                  <span>پلاک:</span>
                  <p className="ml-2">undefiend</p>
                </div>
                <div className="d-flex">
                  <span>تعداد خانوار:</span>
                  <p className="ml-2">undefiend</p>
                </div>
              </div>
            </div>
            <div className="d-flex justify-content-between">
                <div className="d-flex">
                  <span>کدپستی:</span>
                  <p className="ml-2">{this.persionConvertor(billData.zipCode)}</p>
                </div>
                <div className="d-flex">
                  <span>پلاک:</span>
                  <p className="ml-2">undefiend</p>
                </div>

              </div>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = ({ GetBillReducer }) => {
  return {
    billData: GetBillReducer.data,
  };
};
export default connect(mapStateToProps, {
  getBill,
})(ElectricityTemplate);
