import React, { Component } from "react";
import L from "leaflet";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

var mapIcon = L.icon({
  iconUrl: require("../img/placeholder.svg"),
  iconRetinaUrl: require("../img/placeholder.svg"),
  iconSize: [50, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  state = {
    devices: [],
    locations: [
      { lng: 53.9868, lat: 26.5325 },

      { lng: 54, lat: 26.53 },
      { lng: 54.01, lat: 26.55 },
    ],
    lng: 53.9868,
    lat: 26.5325,
    zoom: 13,
  };

  componentDidMount() {
    var requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/all?beginTime=1588686342&endTime=1588689942",
      requestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        this.setState({ devices: result.allData });
        console.log(this.state.devices);
      })
      .catch((error) => console.log("error", error));
  }

  renderMarker = () => {
    return this.state.locations.map((item) => {
      for (let i = 0; i < item.options.rxInfo.length; i++) {
        const element = item[i];
        console.log(element);
      }
    });
  };
  render() {
    const position = [this.state.lat, this.state.lng];
    return (
      <section>
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </section>
    );
  }
}

export default Home;
