import axios from "axios";

export const fetchDevicesID = () => async (dispatch) => {
  const response = await axios.get(
    "https://api.lora.atrovan.com/api/telemetry/devices/id"
  );
  // console.log(response.data);
  dispatch({
    type: "FETCH_DEVICES_ID",
    payload: response.data,
  });


};



export const fetchDevicesData = (id) => async (dispatch) => {
          for (let i = 0; i < id.length; i++) {
        const elementID = id[i];
  const response = await axios.get(
    `https://api.lora.atrovan.com/api/telemetry/device/latest/${elementID}`
  );
  dispatch({
    type: "FETCH_DEVICES_DATA",
    payload: response.data.options,
  })
  }
};



export const fetchAllBuildings = () => async (dispatch) => {


  const response = await axios.get(
    "https://api.lora.atrovan.com/api/buildings/all?password=asd123"
  );
console.log(response);

  dispatch({
    type: "FETCH_ALL_BUILDINGS",
    payload: response.data.data,
  })
  
};


export const fetchBuildingAssets = (uuid) => async (dispatch) => {


  const response = await axios.get(
    "https://api.lora.atrovan.com/api/assets/building/60a7093c-ab34-11ea-855a-47b1e7dfc0a2?limit=100"
  );
console.log(response);

  dispatch({
    type: "FETCH_ALL_BUILDINGS",
    payload: response.data.data,
  })
  
};
var requestOptions = {
  method: 'GET',
  redirect: 'follow'
};

fetch("https://api.lora.atrovan.com/api/assets/building/60a7093c-ab34-11ea-855a-47b1e7dfc0a2?limit=100", requestOptions)
  .then(response => response.text())
  .then(result => console.log(result))
  .catch(error => console.log('error', error));
