import React, { Component } from "react";
import { connect } from "react-redux";
import { getBill } from "../store/actions";
import persianJs from "persianjs";

import moment from "jalali-moment";
import GasBillLogo from "../img/naft_iran-01.svg";

class GasTemplate extends Component {
  state = {};
  timeConverter = (time) => {
    const engDate = moment(time * 1000).format("YYYY/MM/DD");
    console.log(engDate);
    const perSianDate = persianJs(
      moment(`${engDate}`, "YYYY/MM/DD").locale("fa").format("YYYY/MM/DD")
    ).englishNumber()._str;
    return perSianDate;
  };
  persionConvertor = (data) => {
    const persian = persianJs(`${data}`).englishNumber()._str;
    return persian;
  };
  translateNumber = (num) => {
    const translatedNum = persianJs(num).digitsToWords().toString();
    return translatedNum;
  };
  render() {
    const { billData } = this.props;
    return (
      <div className=" col-lg-10 pt-3 pb-3">
        <div className="bill-template gas-template ">
          <div className="col-12 p-1">
            <div className="col-6 p-0 border-gas">
              <div className="d-flex justify-content-between">
                <table className=" table table-bordered w-75">
                  <tbody className="text-left">
                    <tr>
                      <th scope="row" className="light-orange">
                        نام مشترک
                      </th>
                      <td>{billData.owner}</td>
                    </tr>
                    <tr>
                      <th scope="row " className="light-orange">
                        نشانی و کد پستی
                      </th>
                      <td>{billData.address}</td>
                    </tr>
                  </tbody>
                </table>
                <table className=" table table-bordered w-25">
                  <thead>
                    <tr>
                      <th scope="col" className="light-orange">
                        کد آدرس
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{this.persionConvertor(billData.zipCode)}</td>
                    </tr>
                  </tbody>
                </table>
              </div>

              <div className="mt-1 ">
                <table className="table table-bordered ">
                  <thead className="light-orange">
                    <tr>
                      <th scope="col"> منطقه گاز</th>
                      <th scope="col"> اقلیم</th>
                      <th scope="col">شماره اشتراک</th>
                      <th scope="col"> شماره سریال کنتور</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{this.persionConvertor(billData.companyArea)}</td>
                      <td>{}</td>
                      <td>{this.persionConvertor(billData.consumerID)}</td>
                      <td>{this.persionConvertor(billData.factoryID)}</td>
                    </tr>
                  </tbody>
                </table>
              </div>
              <div className="mt-1 ">
                <table className="table table-bordered ">
                  <thead className="light-orange">
                    <tr>
                      <th scope="col"> شماره سری</th>
                      <th scope="col"> شماره پرونده</th>
                      <th scope="col"> نوع مصرف</th>
                      <th scope="col"> گروه</th>
                      <th scope="col"> تعداد واحد</th>
                      <th scope="col"> ظرفیت </th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>
                        {this.persionConvertor(billData.billingSeries
)}
                      </td>
                      <td>
                        {this.persionConvertor(billData.fileNumber
)}
                      </td>
                      <td>{this.persionConvertor(billData.previousReading)}</td>
                      <td>{this.persionConvertor(billData.currentReading)}</td>
                      <td>{this.persionConvertor(billData.currentReading)}</td>
                      <td>{this.persionConvertor(billData.currentReading)}</td>
                    </tr>
                  </tbody>
                </table>
              </div>
              <div className="mt-1 ">
                <table className="table table-bordered ">
                  <thead className="light-orange">
                    <tr>
                      <th scope="col"> رقم پیشین</th>
                      <th scope="col"> رقم فعلی</th>
                      <th scope="col"> تاریخ قرائت پیشین</th>
                      <th scope="col"> تاریخ قرائت فعلی</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>
                        {this.persionConvertor(billData.previousConsumption)}
                      </td>
                      <td>
                        {this.persionConvertor(billData.currentConsumption)}
                      </td>
                      <td>{this.timeConverter(billData.previousReading)}</td>
                      <td>{this.timeConverter(billData.currentReading)}</td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = ({ GetBillReducer }) => {
  return {
    billData: GetBillReducer.data,
  };
};
export default connect(mapStateToProps, {
  getBill,
})(GasTemplate);
