const INITIAL_STATE = {
    data:[]
}

export default function DevicesDataReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case "FETCH_DEVICES_DATA":
      // console.log("FETCH_DEVICES_DATA")
      // console.log(action.payload.options)
      // console.log(state);
      // state.data=[]
      if (state.data.length!=0) {
        
        return {  data: [...state.data, action.payload.options] }
      }

    default:
      return state;
  }
}
