import React, { Component } from "react";
import { Link } from "react-router-dom";
import clock from "../img/clock.svg";
import calendar from "../img/calendar.svg";
import setting from "../img/settings.svg";
import clockYellow from "../img/clock-y.svg";
import calendarYellow from "../img/calendar-y.svg";
import settingYellow from "../img/settings-y.svg";
import logo from "../img/logo.svg";

class Menu extends Component {
  state = {
    selectedTab: 1,
  };
  render() {
    return (
      <aside>
        <header>
          <div className="d-flex align-items-center flex-column mb-5">
            <img src={logo} alt="" width="70px" />
            <h6>سامانه هوشمند کنترل برق</h6>
          </div>
          <div className="menu-tab mt-5">
            <ul className="rtl nav d-flex flex-column">
              <li
                className="nav-item"
                onClick={() => this.setState({ selectedTab: 1 })}
                style={{
                  backgroundColor:
                    this.state.selectedTab === 1 ? "#ffffff3b" : "transparent",
                  borderRadius: this.state.selectedTab === 1 ? "10px" : "none",
                }}
              >
                <NavLink
                  className="nav-link "
                  to="/"
                  style={{
                    color: this.state.selectedTab === 1 ? "#ffdf42" : "white",
                  }}
                >
                  <img
                    src={this.state.selectedTab === 1 ? clockYellow : clock}
                    alt=""
                    className="mr-2"
                  />
                  نمایش لحظه ای
                </NavLink>
              </li>
              <li
                className="nav-item"
                onClick={() => this.setState({ selectedTab: 2 })}
                style={{
                  backgroundColor:
                    this.state.selectedTab === 2 ? "#ffffff3b" : "transparent",
                  borderRadius: this.state.selectedTab === 2 ? "10px" : "none",
                }}
              >
                <NavLink
                  className="nav-link"
                  to="/Gozareshat"
                  // style={{
                  //   color: this.state.selectedTab === 2 ? "#ffdf42" : "white",
                  // }}
                  activeStyle={{
                    fontWeight: "bold",
                    color: "red"
                  }}
                >
                  <img
                    src={
                      this.state.selectedTab === 2 ? calendarYellow : calendar
                    }
                    alt=""
                    className="mr-2"
                  />
                  گزارشات
                </NavLink>
              </li>
              <li
                className="nav-item"
                onClick={() => this.setState({ selectedTab: 3 })}
                style={{
                  backgroundColor:
                    this.state.selectedTab === 3 ? "#ffffff3b" : "transparent",
                  borderRadius: this.state.selectedTab === 3 ? "10px" : "none",
                }}
              >
                <NavLink
                  className="nav-link"
                  to="/Setting"
                  style={{
                    color: this.state.selectedTab === 3 ? "#ffdf42" : "white",
                  }}
                >
                  <img
                    src={this.state.selectedTab === 3 ? settingYellow : setting}
                    alt=""
                    className="mr-2"
                  />
                  تنظیمات
                </NavLink>
              </li>
            </ul>
          </div>
        </header>
      </aside>
    );
  }
}

export default Menu;
