import React, { Component } from "react";
import L from "leaflet";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

var mapIcon = L.icon({
  iconUrl: require("../img/placeholder.svg"),
  iconRetinaUrl: require("../img/placeholder.svg"),
  iconSize: [50, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  state = {
    deviceData: [],
    // info: [],
    locations: [],
    lng: 53.9868,
    lat: 26.5325,
    zoom: 13,
  };

  componentDidMount() {
    var requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/device/data/0011223344556611?beginTime=0&endTime=2588688878",
      requestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        this.setState({
          deviceData: [...this.state.deviceData, ...result.deviceData],
        });
        const array = this.state.deviceData;
        for (let i = 0; i < array.length; i++) {
          const element = array[i].options.rxInfo[0].location;
          // this.setState({ :  });
          console.log(element);
          this.setState({ locations: [...this.state.locations, element] });
          console.log(this.state.locations);
        }
      })
      .catch((error) => console.log("error", error));
  }

  renderMarker = () => {
    // return this.state.locations.map((item) => {
    //   const position = [item.lat, item.lng];
    //   return (
    //     <Marker position={position} icon={mapIcon} key={item.lat}>
    //       <Popup>
    //         A pretty CSS3 popup. <br /> Easily customizable.
    //       </Popup>
    //     </Marker>
    //   );
    // });
  };
  render() {
    const position = [this.state.lat, this.state.lng];
    return (
      <section>
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </section>
    );
  }
}

export default Home;
