import React, { Component } from "react";
class Gozareshat extends Component {
  state = {
    ID: [],
    info: [],
  };

  componentDidMount() {
    var RequestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/devices/id",
      RequestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        const ID = result.devicesID;
        for (let i = 0; i < ID.length; i++) {
          const element = ID[i];
          this.setState({ ID: [...this.state.ID, element] });
        }
        for (let i = 0; i < this.state.ID.length; i++) {
          const element = this.state.ID[i];
          fetch(
            `https://api.lora.atrovan.com/api/telemetry/device/data/${element}?beginTime=0&endTime=2588688878`,
            RequestOptions
          )
            .then((response) => response.json())
            .then((result) => {
              this.setState({
                info: [...this.state.info, result.options],
              });
              // console.log(result.options.rxInfo[0]);
            })
            .catch((error) => console.log("error", error));
        }
      })
      .catch((error) => console.log("error", error));
  }

  render() {
    return <section className="gozareshat-bg"></section>;
  }
}

export default Gozareshat;
