import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import persianJs from "persianjs";
import { setItem } from "../store/actions";
import UserIcon from "../Component/UserIcon";
import CodeIcon from "../Component/CodeIcon";
import TagIcon from "../Component/TagIcon";
import HomeIcon from "../Component/HomeIcon";
class DevicesItem extends Component {
  state = {};
  persionConvertor = (data) => {
    const persian = persianJs(`${data}`).englishNumber()._str;
    return persian;
  };
  runAction =(item) => this.props.setItem(item)
  render() {
    const { item } = this.props;
    return (
      <div className="list-item">
        <div className="info-pop">
          <div className="text-left">
            <UserIcon />
            <span className="info-name">نام مالک:</span>
            <span className="info-num">{item.owner}</span>
          </div>
          <div className="text-left">
            <CodeIcon />

            <span className="info-name">شماره شناسه:</span>
            <span className="info-num">{this.persionConvertor(item.zipCode)}</span>
          </div>
          <div className="text-left">
            <HomeIcon />

            <span className="info-name">نوع کنتور:</span>
            <span className="info-num">{item.subtype}</span>
          </div>
          <div className="text-left">
            <TagIcon />

            <span className="info-name">برند:</span>
            <span className="info-num">{item.brand}</span>
          </div>
        </div>

        <Link
          className="btn "
          to={"/Gozareshat/bill/"}
          onClick={()=>this.runAction(item)}
        >
          مشاهده جزییات
        </Link>
      </div>
    );
  }
}

const mapStateToProps = ({}) => {
  return {};
};
export default connect(mapStateToProps, {
  setItem,
})(DevicesItem);
