import React, { Component } from "react";
import { Link ,Route } from "react-router-dom";
import ResultItem from "../Pages/ResultItem";

import SearchResult from "./SearchResult";

class Gozareshat extends Component {
  _isMounted = false;
  state = {
    ID: [],
    info: [],
    searchResult: [],
    NotFound: null,
    filter: "",
  };

  componentDidMount() {
    this._isMounted = true;
    var RequestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/devices/id",
      RequestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        const ID = result.devicesID;
        for (let i = 0; i < ID.length; i++) {
          const element = ID[i];
          if (this._isMounted) {
            this.setState({ ID: [...this.state.ID, element] });
          }
        }
        for (let i = 0; i < this.state.ID.length; i++) {
          const element = this.state.ID[i];
          fetch(
            `https://api.lora.atrovan.com/api/telemetry/device/latest/${element}`,
            RequestOptions
          )
            .then((response) => response.json())
            .then((result) => {
              if (this._isMounted) {
                if (result.options.object.input2.length >= 20) {
                  this.setState({
                    info: [...this.state.info, result.options],
                  });
                }
              }
            })

            .catch((error) => console.log("error", error));
        }
      })
      .catch((error) => console.log("error", error));
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  handleChange = (event) => {
    this.setState({ filter: event.target.value });
  };
  handleSubmit = (event) => {
    event.preventDefault();
    const search = this.state.filter;
    const info = this.state.info.filter((item) => {
      const hex2ascii = require("hex2ascii");
      const hexPaketID = item.object.input2.slice(2, 34);
      const ID = hex2ascii(hexPaketID);
      ID.includes(search);
      if (ID.includes(search) === true) {
        this.setState({ searchResult: [item] });
        this.setState({ NotFound: "" });
      }
      if (ID.includes(search) === false) {
        this.setState({ NotFound: "موردی یافت نشد!" });
        this.setState({ searchResult: [] });
      }
    });
  };

  renderList = () => {
    const { devices } = this.props;
    return devices.map((item, index) => {
      return (
        <div key={index} className="list-item">
          <div className="info-pop">
            <div className="text-left">
              <UserIcon />
              <span className="info-name">نام مالک:</span>
              <span className="info-num">{item.owner}</span>
            </div>
            <div className="text-left">
              <CodeIcon />

              <span className="info-name">شماره شناسه:</span>
              <span className="info-num">{item.zipCode}</span>
            </div>
            <div className="text-left">
              <HomeIcon />

              <span className="info-name">نوع کنتور:</span>
              <span className="info-num">{item.subtype}</span>
            </div>
            <div className="text-left">
              <TagIcon />

              <span className="info-name">برند:</span>
              <span className="info-num">{item.brand}</span>
            </div>
          </div>

          <Link className="btn " to={`/Gozareshat/${item.applicationID}`}>
            مشاهده جزییات
          </Link>
        </div>)})
  };

  render() {
    return (
      <div className="gozareshat-bg p-3 col-lg-10">
        <div className="w-100 form-box">
          <h6>برای جستجوی کنتور مورد نظر شناسه کنتور را وارد کنید.</h6>
          <form
            onSubmit={this.handleSubmit}
            className="form-inline my-2 my-lg-0 search-form"
          >
            <input
              className="form-control mr-sm-2 "
              value={this.state.filter}
              onChange={this.handleChange}
              // onKeyPress={this.handleKeyPress}
              type="number"
              placeholder="شماره شناسه کنتور..."
              aria-label="Search"
            />
            <button
              className="btn btn-outline-success my-2 my-sm-0"
              type="submit"
            >
              جستجو
            </button>
          </form>
        </div>
        {this.state.searchResult.length !== 0 ||
        this.state.NotFound !== null ? (
          <SearchResult
            searchResult={this.state.searchResult}
            filter={this.state.filter}
            NotFound={this.state.NotFound}
          />
        ) : (
          <div className="list-box mt-3">{this.renderList()}</div>
        )}
      </div>
    );
  }
}

export default Gozareshat;
