import React, { Component } from "react";
import L from "leaflet";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

var mapIcon = L.icon({
  iconUrl: require("../img/placeholder.svg"),
  iconRetinaUrl: require("../img/placeholder.svg"),
  iconSize: [50, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  state = {
    deviceData: [],
    ID: [],
    info: [],
    locations: [],
    lng: 53.9868,
    lat: 26.5325,
    zoom: 13,
  };

  componentDidMount() {
    var RequestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/devices/id",
      RequestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        const ID = result.devicesID;
        for (let i = 0; i < ID.length; i++) {
          const element = ID[i];
          this.setState({ ID: [...this.state.ID, element] });
        }
        for (let i = 0; i < this.state.ID.length; i++) {
          const element = this.state.ID[i];
          fetch(
            `https://api.lora.atrovan.com/api/telemetry/device/latest/${element}`,
            RequestOptions
          )
            .then((response) => response.json())
            .then((result) => {
              // console.log(result);

              this.setState({
                locations: [
                  ...this.state.deviceData,
                  result.options.rxInfo[0].location,
                ],
              });
              this.setState({
                info: [...this.state.deviceData, result.options],
              });
              // console.log(result.options.rxInfo[0]);
            })
            .catch((error) => console.log("error", error));
        }
      })
      .catch((error) => console.log("error", error));
  }

  renderMarker = () => {
    return this.state.info.map((item) => {
      const position = [item.rxInfo[0].location.latitude, item.rxInfo[0].location.longitude];
      console.log(item);
      return (
        <Marker position={position} icon={mapIcon} key={item.gatewayID}>
          <Popup>
            <span className="pop-title">اطلاعات دستگاه</span>

            <div className="info-pop">
              <div>
                <span className="info-name">نام دستگاه:</span>
                <span className="info-num">{item.name}</span>
              </div>
              <div>
                <span className="info-name">عدد دستگاه:</span>
                <span className="info-num">{item.loRaSNR}</span>
              </div>
            </div>
          </Popup>
        </Marker>
      );
    });
  };
  render() {
    const position = [this.state.lat, this.state.lng];
    return (
      <section>
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </section>
    );
  }
}

export default Home;
