import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import persianJs from "persianjs";
import { setItem } from "../store/actions";
import UserIcon from "../asset/icons/UserIcon";
import CodeIcon from "../asset/icons/CodeIcon";
import TagIcon from "../asset/icons/TagIcon";
import HomeIcon from "../asset/icons/HomeIcon";
class DevicesItem extends Component {
  state = {};
  persionConvertor = (data) => {
    const persian = persianJs(`${data}`).englishNumber()._str;
    return persian;
  };
  runAction = (item) => this.props.setItem(item);
  getType = (type) => {
    if (type === "electricity") {
      return "برق";
    }
    if (type === "water") {
      return "آب";
    }
    if (type === "gas") {
      return "گاز";
    }
  };
  render() {
    const { item } = this.props;
    return (
      <div className="list-item col-4">
        <div className="info-pop">
          <div className="text-left">
            <UserIcon />
            <span className="info-name">نام مالک:</span>
            <span className="info-num">{item.owner}</span>
          </div>
          <div className="text-left">
            <CodeIcon />

            <span className="info-name">شماره شناسه:</span>
            <span className="info-num">
              {this.persionConvertor(item.zipCode)}
            </span>
          </div>
          <div className="text-left">
            <HomeIcon />

            <span className="info-name">نوع کنتور:</span>
            <span className="info-num">{this.getType(item.type)}</span>
          </div>
          <div className="text-left">
            <TagIcon />

            <span className="info-name">برند:</span>
            <span className="info-num">{item.brand}</span>
          </div>
        </div>

        <Link
          className="btn "
          to={"/reports/bill/"}
          onClick={() => this.runAction(item)}
        >
          مشاهده جزییات
        </Link>
      </div>
    );
  }
}

const mapStateToProps = ({}) => {
  return {};
};
export default connect(mapStateToProps, {
  setItem,
})(DevicesItem);
