import React, { Component } from "react";
import L from "leaflet";
import { fetchDevicesID, fetchDevicesData, fetchAllBuildings,fetchBuildingAssets } from "../store/actions";
import { connect } from "react-redux";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

var mapIcon = L.icon({
  iconUrl: require("../img/placeholder.svg"),
  iconRetinaUrl: require("../img/placeholder.svg"),
  iconSize: [50, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ID: [],
      info:[],
      lng: 53.982,
      lat: 26.5425,
      zoom: 13,
    };
  }
  _isMounted = false;

  componentDidMount() {
    this._isMounted = true;
    if (this._isMounted) {
      // console.log(this.props);
      this.props.fetchDevicesID();
      // this.props.fetchDevicesData(this.props.id);
      this.props.fetchAllBuildings();
      this.props.fetchBuildingAssets(this.props.uuid);
    }

  }
 
  componentWillUnmount() {
    this._isMounted = false;
  }
  renderMarker = () => {
    return this.props.assets.map((item, index) => {
  
  
          const position = [
            item.latitude,
            item.longitude,
          ];
          additional: "{}"
address: "میدان بهارستان"
city: "کیش"
cityId: "1162"
country: "ایران"
countryId: "1"
createdAt: 1592060392
description: "اینجا ایران است"
latitude: 26.559277
longitude: 53.944167
name: "برج مسعود"
phone: "44589645"
region: ""
state: "هرمزگان"
stateId: "1157"
subtype: "شخصی"
type: "مسکونی"
uuid: "8903623c-ad86-11ea-b951-9f459a2ec1cf"
          return (
            <Marker position={position} icon={mapIcon} key={index}>
              <Popup>
                <span className="pop-title">اطلاعات دستگاه</span>
  
                <div className="info-pop">
                  <div className="text-left">
                    <span className="info-name">نام مالک:</span>
                    <span className="info-num">{item.rxInfo[0].name}</span>
                  </div>
                  <div className="text-left">
                    <span className="info-name">شماره شناسه:</span>
                    <span className="info-num">{packetID}</span>
                  </div>
                  <div className="text-left">
                    <span className="info-name">ولتاژ:</span>
                    <span className="info-num">{volt}</span>
                  </div>
                  <div className="text-left">
                    <span className="info-name">آمپر:</span>
                    <span className="info-num">{amper}</span>
                  </div>
                  <div className="text-right">
                    <span className="info-name">P_1_8_0:</span>
                    <span className="info-num">{p180}</span>
                  </div>
                  <div className="text-right">
                    <span className="info-name">P_1_8_1:</span>
                    <span className="info-num">{p181}</span>
                  </div>
                  <div className="text-right">
                    <span className="info-name">P_1_8_2:</span>
                    <span className="info-num">{p182}</span>
                  </div>
                  <div className="text-right">
                    <span className="info-name">P_1_8_3:</span>
                    <span className="info-num">{p183}</span>
                  </div>
                  <div className="text-right">
                    <span className="info-name">P_1_8_4:</span>
                    <span className="info-num">{p184}</span>
                  </div>
                </div>
              </Popup>
            </Marker>
          );
 
      });
  };
  render() {
    console.log(this.props.assets);
    const position = [this.state.lat, this.state.lng];
    return (
      <div className="col-lg-10 p-0">
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </div>
    );
  }
}

const mapStateToProps = ({DevicesIDReducer,DevicesDataReducer,AllBuildingsReducer,BuildingAssetsReducer}) => {
  // console.log({AllBuildingsReducer});
  return {
    // id: state.ID.devicesID
    id: DevicesIDReducer.data,
    // info : DevicesDataReducer.data,
    uuid: AllBuildingsReducer.data[0].uuid,
    assets: BuildingAssetsReducer.data
  };
};

export default connect(mapStateToProps, { fetchDevicesID, fetchBuildingAssets ,fetchAllBuildings})(
  Home
);
