import React, { Component } from "react";

import { setItem, setDate, getBill } from "../store/actions";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import DatePicker from "../Component/DatePicker";

import moment from "jalali-moment";
import waterBillLogo from "../asset/img/Ab-o-Fazelab-01.svg";
import GasBillLogo from "../asset/img/naft_iran-01.svg";
import ElecBillLogo from "../asset/img/Tavanir_logo-01.svg";
import UserIcon from "../asset/icons/UserIcon";
import CodeIcon from "../asset/icons/CodeIcon";
import TagIcon from "../asset/icons/TagIcon";
import HomeIcon from "../asset/icons/HomeIcon";

class GetBill extends Component {
  renderBtn = () => {
    if (this.props.dateRange.dateRange) {
      const { dateRange } = this.props.dateRange;
      if ((dateRange.from, dateRange.to)) {
        return (
          <Link
            className="btn calendar-btn"
            onClick={() => this.getBill(dateRange.from, dateRange.to)}
            to={this.getTemplate()}
          >
            نمایش قبض
          </Link>
        );
      } else {
        return <a className="btn calendar-btn deactive"> نمایش قبض</a>;
      }
    } else {
      return <a className="btn calendar-btn deactive"> نمایش قبض</a>;
    }
  };
  getTemplate = () => {
    if (this.props.newItem.type === "electricity") {
      return `/reports/bill/electricity/${this.props.newItem.uuid}`;
    }
    if (this.props.newItem.type === "water") {
      return `/reports/bill/water/${this.props.newItem.uuid}`;
    }
    if (this.props.newItem.type === "gas") {
      return `/reports/bill/gas/${this.props.newItem.uuid}`;
    }
  };
  getBill = (from, to) => {
    const beginTime =
      new Date(
        moment
          .from(`${from.year}.${from.month}.${from.day}`, "fa", "YYYY/MM/DD")
          .format("YYYY/MM/DD")
      ).getTime() / 1000;

    const endTime =
      new Date(
        moment
          .from(`${to.year}.${to.month}.${to.day}`, "fa", "YYYY/MM/DD")
          .format("YYYY/MM/DD")
      ).getTime() / 1000;

    this.props.getBill({
      deviceID: this.props.newItem.uuid,
      beginTime,
      endTime,
    });
  };
  getLogo = () => {
    if (this.props.newItem.type === "electricity") {
      return ElecBillLogo;
    }
    if (this.props.newItem.type === "water") {
      return waterBillLogo;
    }
    if (this.props.newItem.type === "gas") {
      return GasBillLogo;
    }
  };
  getType = (item) => {
    if (type === "electricity") {
      return "برق";
    }
    if (type === "water") {
      return "آب";
    }
    if (type === "gas") {
      return "گاز";
    }
  };
  render() {
    return (
      <div className=" content-width">
        <div className="bill info-pop">
          <div className="date-range-title">
            ابتدا و انتهای بازه زمانی را انتخاب کنید.
          </div>
          <div className="text-left mb-5">
            <img src={this.getLogo()} width="70px" alt="" />
          </div>
          <div className="text-left">
            <UserIcon />
            <span className="info-name">نام مالک:</span>
            <span className="info-num">{this.props.newItem.owner}</span>
          </div>
          <div className="text-left">
            <CodeIcon />

            <span className="info-name">شماره شناسه:</span>
            <span className="info-num">{this.props.newItem.zipCode}</span>
          </div>
          <div className="text-left">
            <HomeIcon />

            <span className="info-name">نوع کنتور:</span>
            <span className="info-num">{this.getType(this.props.newItem.type)}</span>
          </div>
          <div className="text-left">
            <TagIcon />

            <span className="info-name">برند:</span>
            <span className="info-num">{this.props.newItem.brand}</span>
          </div>

          {this.renderBtn()}
          <DatePicker setDate={this.props.setDate} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = ({
  SetItemReducer,
  SetDateReducer,
  GetBillReducer,
}) => {
  return {
    newItem: SetItemReducer.data.item,
    dateRange: SetDateReducer.data,
    billData: GetBillReducer,
  };
};
export default connect(mapStateToProps, {
  setItem,
  setDate,
  getBill,
})(GetBill);
