import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import Calendar from "../icons/calendar"
import Clock from "../icons/clock"
import Setting from "../icons/calendar"
import logo from "../img/logo.svg";

class Menu extends Component {
  render() {
    return (
      <aside>
        <header>
          <div className="d-flex align-items-center flex-column mb-5">
            <img src={logo} alt="" width="70px" />
            <h6>سامانه هوشمند کنترل برق</h6>
          </div>
          <div className="menu-tab mt-5">
            <ul className="rtl nav d-flex flex-column">
              <li className="nav-item">
                <NavLink
                  className="nav-link "
                  exact
                  to="/"
                  activeStyle={{
                    color: "#ffdf42",
                  }}
                >
                  <Clock/>
                  نمایش لحظه ای
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink
                  className="nav-link"
                  to="/Gozareshat"
                  activeStyle={{
                    color: "#ffdf42",
                  }}
                >
                  <Calendar/>
                  گزارشات
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink
                  className="nav-link"
                  to="/Setting"
                  activeStyle={{
                    color: "#ffdf42",
                  }}
                >
                  <Setting/>
                  تنظیمات
                </NavLink>
              </li>
            </ul>
          </div>
        </header>
      </aside>
    );
  }
}

export default Menu;
