import React, { useState, useEffect } from "react";
import "react-modern-calendar-datepicker/lib/DatePicker.css";
import { Calendar } from "react-modern-calendar-datepicker";
import { connect } from "react-redux";
const Taghvim = (props) => {
  // ✅ a change in default state: { from: ..., to: ... }
  const [selectedDayRange, setSelectedDayRange] = useState({
    from: null,
    to: null,
  });
  // const { setDate } = props;

  // useEffect(() => {
  //   if ((selectedDayRange.from, selectedDayRange.to)) {
  //     setDate(selectedDayRange);
  //   }
  // }, [setDate]);

  return (
    <Calendar
      value={selectedDayRange}
      onChange={setSelectedDayRange}
      shouldHighlightWeekends
      locale="fa"
      calendarTodayClassName="custom-today-day" // also this
      colorPrimary="#bace24" // added this
      inputPlaceholder="Select a day range"
      colorPrimaryLight="rgb(196 216 46 / 26%)" // and this
    />
  );
};

const ShowTodos = (props) =>{
  // const { fetchTodos } = props
  useEffect(()=>{
    setDate()
    // eslint-disable-next-line import/no-extraneous-dependencies
  },[])
} 
const mapStateToProps = (state)=>{
 return {
   todos:Object.values(state.todos),
   currentUserId:state.authenticate.userId
 }
}

export default connect(mapStateToProps,{setDate})(Taghvim)
