import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { withRouter } from "react-router-dom";
import {
  fetchAllBuildings,
  fetchBuildingDevices,
  searchBuildingDevices,
} from "../store/actions";

import DevicesItem from "../Component/DevicesItem";
import SearchIcon from "../asset/icons/SearchIcon";

const Reports = (props) => {
  state = {
    searchText: "",
  };
  const [searchText, setSearchText] = useState("");

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchAllBuildings());
    dispatch(fetchBuildingDevices(uuid.data));
    validate();
  }, []);
  const uuid = useSelector((state) => state.AllBuildingsReducer);
  const buildingDevices = useSelector(
    (state) => state.BuildingDevicesReducer.data.data
  );
  const searchResult = useSelector((state) => state.SearchBuildingReducer.data);

  const validate = () => {
    document.addEventListener("DOMContentLoaded", function () {
      var elements = document.getElementsByTagName("INPUT");
      for (var i = 0; i < elements.length; i++) {
        elements[i].oninvalid = function (e) {
          e.target.setCustomValidity("");
          if (!e.target.validity.valid) {
            e.target.setCustomValidity("لطفا این بخش را پر کنید");
          }
        };
        elements[i].oninput = function (e) {
          e.target.setCustomValidity("");
        };
      }
    });
  };
  const handleChange = (event) => {
    setSearchText( event.target.value );
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    validate();
    searchBuildingDevices({
      buildingId: uuid,
      searchText: searchText,
    });
    const path = `/reports/search/${searchText}`;
    props.history.push(path);
    document.getElementById("mainInput").reset();
  };

  const renderList = () => {
    const { buildingDevices } = this.props;
    if (buildingDevices) {
      return buildingDevices.map((item, index) => {
        return <DevicesItem key={index} item={item} />;
      });
    }
  };

  return (
    <div className="Reports-bg content-width ">
      <div className=" form-box">
        <h6>برای جستجوی کنتور مورد نظر شناسه کنتور را وارد کنید.</h6>
        <form
          onSubmit={this.handleSubmit}
          id="mainInput"
          className="form-inline my-2 my-lg-0 search-form"
        >
          <input
            className="form-control mr-sm-2 "
            value={this.state.searchText}
            onChange={this.handleChange}
            type="number"
            placeholder="شماره شناسه کنتور..."
            aria-label="Search"
            required
          />

          <button
            className="btn btn-outline-success my-2 my-sm-0"
            type="submit"
          >
            جستجو
            <SearchIcon />
          </button>
        </form>
      </div>
      <div className="list-box" id="style-7">
        {this.renderList()}
      </div>
    </div>
  );
};
const mapStateToProps = ({
  AllBuildingsReducer,
  BuildingDevicesReducer,
  SearchBuildingReducer,
}) => {
  return {
    // uuid: AllBuildingsReducer.data,
    uuid: "b15406c0-d01c-11ea-91d5-53a3a6b91e65",
    buildingDevices: BuildingDevicesReducer.data.data,
    searchResult: SearchBuildingReducer.data,
  };
};

export default withRouter(Reports);
