import React, { Component } from "react";
import waterBillLogo from "../img/Ab-o-Fazelab-01.svg";
import DatePicker from "../Component/DatePicker";
import { connect } from "react-redux";
import { setItem } from "../store/actions";
import UserIcon from "../Component/UserIcon";
import CodeIcon from "../Component/CodeIcon";
import TagIcon from "../Component/TagIcon";
import HomeIcon from "../Component/HomeIcon";

class WaterBill extends Component {
  state = {};

  renderBill = () => {
    if (this.state.bill) {
      return (
        <div className="water-bill p-2">
          <div>
            <img src={waterBillLogo} width="70px" alt="" /> شرکت آب و فاضلاب
            ایران
          </div>
        </div>
      );
    }
  };
  getDate = (dateRange) => {
    console.log(dateRange);
  };

  render() {
    console.log(this.props.newItem);
    return (
      <div className=" col-lg-10">
        <div className="list-item">
          <div className=" info-pop">
            <div className="text-left">
              <UserIcon />
              <span className="info-name">نام مالک:</span>
              <span className="info-num">{this.props.newItem.owner}</span>
            </div>
            <div className="text-left">
              <CodeIcon />

              <span className="info-name">شماره شناسه:</span>
              <span className="info-num">{this.props.newItem.zipCode}</span>
            </div>
            <div className="text-left">
              <HomeIcon />

              <span className="info-name">نوع کنتور:</span>
              <span className="info-num">{this.props.newItem.subtype}</span>
            </div>
            <div className="text-left">
              <TagIcon />

              <span className="info-name">برند:</span>
              <span className="info-num">{this.props.newItem.brand}</span>
            </div>
          </div>
        </div>
          <DatePicker getDate={this.getDate} />
        {this.renderBill()}
      </div>
    );
  }
}

const mapStateToProps = ({ SetItemReducer }) => {
  return {
    newItem: SetItemReducer.data.item,
  };
};
export default connect(mapStateToProps, {
  setItem,
})(WaterBill);
