import React, { Component } from "react";
import L from "leaflet";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

var mapIcon = L.icon({
  iconUrl: require("../img/placeholder.svg"),
  iconRetinaUrl: require("../img/placeholder.svg"),
  iconSize: [50, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  state = {
    ID: [],
    info: [],
    lng: 53.9868,
    lat: 26.5325,
    zoom: 13,
  };

  componentDidMount() {
    var RequestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/devices/id",
      RequestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        const ID = result.devicesID;
        for (let i = 0; i < ID.length; i++) {
          const element = ID[i];
          this.setState({ ID: [...this.state.ID, element] });
        }
        for (let i = 0; i < this.state.ID.length; i++) {
          const element = this.state.ID[i];
          fetch(
            `https://api.lora.atrovan.com/api/telemetry/device/latest/${element}`,
            RequestOptions
          )
            .then((response) => response.json())
            .then((result) => {
              // console.log(result.options);
              this.setState({
                info: [...this.state.info, result.options],
              });
            })
            .catch((error) => console.log("error", error));
        }
      })
      .catch((error) => console.log("error", error));
  }

  renderMarker = () => {
      return this.state.info.map((item, index) => {
        // console.log("item");
        const hex2ascii = require("hex2ascii");
        const input2 = item.object.input2.slice(150, 164);
        const hexPaketID = item.object.input2.slice(2, 34);
        const packetID = hex2ascii(hexPaketID);
        // console.log(packetID);
        const volt = hex2ascii(input2);
        const position = [
          item.rxInfo[0].location.latitude,
          item.rxInfo[0].location.longitude,
        ];
        return (
          <Marker position={position} icon={mapIcon} key={item._id}>
            <Popup>
              <span className="pop-title">اطلاعات دستگاه</span>

              <div className="info-pop">
                <div>
                  <span className="info-name">نام دستگاه:</span>
                  <span className="info-num">{item.rxInfo[0].name}</span>
                </div>
                <div>
                  <span className="info-name">شماره شناسه:</span>
                  <span className="info-num">{packetID}</span>
                </div>
                <div>
                  <span className="info-name">میزان مصرف:</span>
                  <span className="info-num">{volt}</span>
                </div>
              </div>
            </Popup>
          </Marker>
        );
      });
  };
  render() {
    const position = [this.state.lat, this.state.lng];
    return (
      <section>
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </section>
    );
  }
}

export default Home;
