import React, { Component } from "react";
class Gozareshat extends Component {
  _isMounted = false;
  state = {
    ID: [],
    info: [],
    input2: [],
    filter: "",
  };

  componentDidMount() {
    this._isMounted = true;
    var RequestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/devices/id",
      RequestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        const ID = result.devicesID;
        for (let i = 0; i < ID.length; i++) {
          const element = ID[i];
          if (this._isMounted) {
            this.setState({ ID: [...this.state.ID, element] });
          }
        }
        for (let i = 0; i < this.state.ID.length; i++) {
          const element = this.state.ID[i];
          fetch(
            `https://api.lora.atrovan.com/api/telemetry/device/latest/${element}`,
            RequestOptions
          )
            .then((response) => response.json())
            .then((result) => {
              if (this._isMounted) {
                this.setState({
                  info: [...this.state.info, result.options],
                });

                

              }
            })

            .catch((error) => console.log("error", error));
        }
      })
      .catch((error) => console.log("error", error));


  }

  componentWillUnmount() {
    this._isMounted = false;
  }
  handleChange = (event) => {
    this.handleState()

    this.setState({ filter: event.target.value });
  };


  handleState=()=>{
    const array= this.state.info
    for (let i = 0; i < array.length; i++) {
      const element = array[i];
      const inputVal = element.object.input2
      this.setState({
        input2: [...this.state.input2, inputVal],
      });
    }
  }
  render() {
    const { filter, input2 } = this.state;
    const lowercasedFilter = filter.toLowerCase();
    const filteredData = input2.filter(item => {
      return Object.keys(item).some(key =>
        item[key].toLowerCase().includes(lowercasedFilter)
      );
    });
    console.log(this.state.input2);
    return (
      <section className="gozareshat-bg p-3">
        <div className="w-100 form-box">
          <h6>برای جستجوی کنتور مورد نظر شناسه کنتور را وارد کنید.</h6>
          <form className="form-inline my-2 my-lg-0 search-form">
            <input
              className="form-control mr-sm-2 "
              // value={filter}
              onChange={this.handleChange}
              type="search"
              placeholder="شماره شناسه کنتور..."
              aria-label="Search"
            />
            <button
              className="btn btn-outline-success my-2 my-sm-0"
              type="submit"
            >
              جستجو
            </button>
          </form>
        
        </div>
      </section>
    );
  }
}

export default Gozareshat;
