import React, { Component } from "react";
import { connect } from "react-redux";
import { getBill } from "../store/actions";
import persianJs from "persianjs";

import moment from "jalali-moment";
import waterBillLogo from "../img/Ab-o-Fazelab-01.svg";

class WaterTemplate extends Component {
  state = {};
  timeConverter = (time) => {
    // var a = new Date(UNIX_timestamp * 1000);
    // var months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    // var year = a.getFullYear();
    // var month = months[a.getMonth()];
    // var date = a.getDate();

    // var time = year + "/"+month+ "/" +date ;
    // return time;
    const engDate = new Date(time * 1000).toLocaleDateString("en-US");
    const perSianDate = moment
      .from(`{engDate}`, "YYYY/MM/DD")
      .format("YYYY/MM/DD");
    return perSianDate;
  };
  render() {
    const { billData } = this.props;
    // console.log(persianJs(`${billData.previousReading}`).englishNumber())
    return (
      <div className=" col-lg-10 pt-3 pb-3">
        <div className="bill-template">
          <div className="mb-2">
            <img src={waterBillLogo} width="40px" alt="" />
            شرکت آب و فاضلاب
          </div>
          <div>
            <div className="d-flex justify-content-between water-border">
              <div className="bill-header">
                <div>
                  <span>مشترک گرامی:</span> <p>{billData.owner}</p>
                </div>
                <div>
                  <span>شماره اشتراک:</span>{" "}
                  <p>
                    {persianJs(`${billData.consumerID}`).englishNumber()._str}
                  </p>
                </div>
                <div>
                  <span className="red">شماره پرونده:</span>
                  <p>
                    {persianJs(`${billData.fileNumber}`).englishNumber()._str}
                  </p>
                </div>
              </div>
              <div className="bill-header">
                <div>
                  <span>کد و نوع تعرفه:</span>{" "}
                  <p>
                    {
                      persianJs(`${billData.tariffCodeType}`).englishNumber()
                        ._str
                    }
                  </p>
                </div>
                <div>
                  <span>کدپستی:</span>{" "}
                  <p>{persianJs(`${billData.zipCode}`).englishNumber()._str}</p>
                </div>
                <div>
                  <span>نشانی:</span> <p>{billData.address}</p>
                </div>
              </div>
              <div className="bill-header">
                <div>
                  <span> مامور قرائت:</span>
                  <p>{billData.readingOperator}</p>
                </div>
                <div>
                  <span>محدوده قرائت:</span>{" "}
                  <p>{persianJs(`${billData.zipCode}`).englishNumber()._str}</p>
                </div>
              </div>
              <div className="bill-header">
                <div>
                  <span>تاریخ صدور قبض:</span>{" "}
                  <p>{this.timeConverter(billData.issueDate)}</p>
                </div>
                <div>
                  <span>مراجعه بعدی:</span>{" "}
                  <p>{this.timeConverter(billData.nextReading)}</p>
                </div>
              </div>
            </div>

            <div className="d-flex">
              <div className="col-2 p-0">
                <table className="table table-bordered ">
                  <thead>
                    <tr>
                      <th scope="col">واحد</th>
                      <th scope="col">سکونت</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <th scope="row">{billData.consumerType}</th>
                      <td>
                        {
                          persianJs(`${billData.consumerCount}`).englishNumber()
                            ._str
                        }
                      </td>
                    </tr>
                  </tbody>
                </table>
                <table className="table table-bordered">
                  <thead>
                    <tr>
                      <th colSpan="3" className="text-center">
                        قطر انشعاب
                      </th>
                    </tr>
                    <tr>
                      <th scope="col">آب</th>
                      <th scope="col">فاضلاب</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>
                        {
                          persianJs(`${billData.waterDiameter}`).englishNumber()
                            ._str
                        }
                      </td>
                      <td>
                        {
                          persianJs(`${billData.sewerDiameter}`).englishNumber()
                            ._str
                        }
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
              <div className="col-4 p-0">
                <table className="table table-bordered">
                  <thead>
                    <tr>
                      <th colSpan="3" className="text-center">
                        قرائت
                      </th>
                    </tr>
                    <tr>
                      <th scope="col"></th>
                      <th scope="col">قبلی</th>
                      <th scope="col">فعلی</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <th scope="row">تاریخ</th>
                      <td>
                        {
                          persianJs(
                            `${billData.previousReading}`
                          ).englishNumber()._str
                        }
                      </td>
                      <td>
                        {
                          persianJs(
                            `${billData.currentReading}`
                          ).englishNumber()._str
                        }
                      </td>
                    </tr>
                  </tbody>
                </table>
                <table className="table table-bordered ">
                  <thead>
                    <tr>
                      <th scope="col">ظرفیت قراردادی</th>
                      <th scope="col">شماره بدنه کنتور</th>
                      <th scope="col">وضعیت کنتور</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td scope="row">
                        {
                          persianJs(
                            `${billData.contractualCapacity}`
                          ).englishNumber()._str
                        }
                      </td>
                      <td>
                        {
                          persianJs(`${billData.factoryID}`).englishNumber()
                            ._str
                        }
                      </td>
                      <td>
                        {
                          persianJs(`${billData.meterStatus}`).englishNumber()
                            ._str
                        }
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
              <div className="col-3 p-0">
                <table className="table table-bordered emty-h"></table>
                <table className="table table-bordered blue-bg">
                  <thead>
                    <tr>
                      <th scope="col">شناسه قبض</th>
                      <th scope="col">شناسه پرداخت</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td scope="row">
                        {
                          persianJs(`${billData.billingID}`).englishNumber()
                            ._str
                        }
                      </td>
                      <td>
                        {
                          persianJs(`${billData.paymentID}`).englishNumber()
                            ._str
                        }
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = ({ GetBillReducer }) => {
  return {
    billData: GetBillReducer.data,
  };
};
export default connect(mapStateToProps, {
  getBill,
})(WaterTemplate);
