import React, { Component } from "react";
import L from "leaflet";
import { fetchDevicesID, fetchDevicesData } from "../store/actions";
import { connect } from "react-redux";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

var mapIcon = L.icon({
  iconUrl: require("../img/placeholder.svg"),
  iconRetinaUrl: require("../img/placeholder.svg"),
  iconSize: [50, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ID: [],
      info:[],
      lng: 53.982,
      lat: 26.5425,
      zoom: 13,
    };
  }
  _isMounted = false;

  componentDidMount() {
    this._isMounted = true;
    if (this._isMounted) {
      console.log(this.props);
      this.props.fetchDevicesID();
      this.props.fetchDevicesData(this.props.id);
    }

  }
 
  componentWillUnmount() {
    this._isMounted = false;
  }
  renderMarker = () => {
    console.log(this.props.info);
    return this.state.info.map((item, index) => {
      if (item.object.input2.length >= 20) {
        const hex2ascii = require("hex2ascii");

        const hexPaketID = item.object.input2.slice(2, 34);
        const packetID = hex2ascii(hexPaketID);

        const hexVolt = item.object.input2.slice(150, 164);
        const volt = hex2ascii(hexVolt);

        const hexAmper = item.object.input2.slice(136, 150);
        const amper = hex2ascii(hexAmper);

        const hexP180 = item.object.input2.slice(36, 56);
        const p180 = hex2ascii(hexP180);

        const hexP181 = item.object.input2.slice(56, 76);
        const p181 = hex2ascii(hexP181);

        const hexP182 = item.object.input2.slice(76, 96);
        const p182 = hex2ascii(hexP182);

        const hexP183 = item.object.input2.slice(96, 116);
        const p183 = hex2ascii(hexP183);

        const hexP184 = item.object.input2.slice(116, 136);
        const p184 = hex2ascii(hexP184);

        const position = [
          item.rxInfo[0].location.latitude,
          item.rxInfo[0].location.longitude,
        ];
        return (
          <Marker position={position} icon={mapIcon} key={index}>
            <Popup>
              <span className="pop-title">اطلاعات دستگاه</span>

              <div className="info-pop">
                <div className="text-left">
                  <span className="info-name">نام دستگاه:</span>
                  <span className="info-num">{item.rxInfo[0].name}</span>
                </div>
                <div className="text-left">
                  <span className="info-name">شماره شناسه:</span>
                  <span className="info-num">{packetID}</span>
                </div>
                <div className="text-left">
                  <span className="info-name">ولتاژ:</span>
                  <span className="info-num">{volt}</span>
                </div>
                <div className="text-left">
                  <span className="info-name">آمپر:</span>
                  <span className="info-num">{amper}</span>
                </div>
                <div className="text-right">
                  <span className="info-name">P_1_8_0:</span>
                  <span className="info-num">{p180}</span>
                </div>
                <div className="text-right">
                  <span className="info-name">P_1_8_1:</span>
                  <span className="info-num">{p181}</span>
                </div>
                <div className="text-right">
                  <span className="info-name">P_1_8_2:</span>
                  <span className="info-num">{p182}</span>
                </div>
                <div className="text-right">
                  <span className="info-name">P_1_8_3:</span>
                  <span className="info-num">{p183}</span>
                </div>
                <div className="text-right">
                  <span className="info-name">P_1_8_4:</span>
                  <span className="info-num">{p184}</span>
                </div>
              </div>
            </Popup>
          </Marker>
        );
      }
    });
  };
  render() {
    const position = [this.state.lat, this.state.lng];
    return (
      <div className="col-lg-10 p-0">
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </div>
    );
  }
}

const mapStateToProps = ({DevicesIDReducer,DevicesDataReducer}) => {
  console.log({DevicesDataReducer});
  return {
    // id: state.ID.devicesID
    id: DevicesIDReducer.data,
    info : [DevicesDataReducer.data]
  };
};

export default connect(mapStateToProps, { fetchDevicesID, fetchDevicesData })(
  Home
);
