import React, { Component } from "react";
class Gozareshat extends Component {
  _isMounted = false;
  state = {
    ID: [],
    info: [],
    packetID: [],
    filter: "",
  };

  componentDidMount() {
    this._isMounted = true;
    var RequestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://api.lora.atrovan.com/api/telemetry/devices/id",
      RequestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        const ID = result.devicesID;
        for (let i = 0; i < ID.length; i++) {
          const element = ID[i];
          if (this._isMounted) {
            this.setState({ ID: [...this.state.ID, element] });
          }
        }
        for (let i = 0; i < this.state.ID.length; i++) {
          const element = this.state.ID[i];
          fetch(
            `https://api.lora.atrovan.com/api/telemetry/device/latest/${element}`,
            RequestOptions
          )
            .then((response) => response.json())
            .then((result) => {
              if (this._isMounted) {
                if (result.options.object.input2.length >= 20) {
                  this.setState({
                    info: [...this.state.info, result.options],
                  });
                }
              }
            })

            .catch((error) => console.log("error", error));
        }
      })
      .catch((error) => console.log("error", error));
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  handleChange = (event) => {
    this.setState({ filter: event.target.value });
  };
  handleSubmit = (event) => {
    event.preventDefault();
    const search = this.state.filter;
    console.log(search);
    const info = this.state.info.filter((item) => {
      console.log(item);
      const hex2ascii = require("hex2ascii");
      const hexPaketID = item.object.input2.slice(2, 34);
      const ID = hex2ascii(hexPaketID);
      ID.includes(search);
      if (ID.includes(search)) {
        this.setState({ info: [item] });
      }
      if (ID.includes(search) === false) {
        this.setState({ info: [...this.state.info] });
      }
    });
    console.log(info);
  };

  renderList = () => {
    return this.state.info.map((item, index) => {
      if (item.object.input2.length >= 20) {
        const hex2ascii = require("hex2ascii");

        const hexPaketID = item.object.input2.slice(2, 34);
        const packetID = hex2ascii(hexPaketID);

        const hexVolt = item.object.input2.slice(150, 164);
        const volt = hex2ascii(hexVolt);

        const hexAmper = item.object.input2.slice(136, 150);
        const amper = hex2ascii(hexAmper);
        if (this.state.info.length !== 0) {
          console.log("aaaaaaaa");
          return (
            <div key={index} className="list-item">
              <div className="info-pop">
                <div className="text-left">
                  <span className="info-name">نام دستگاه:</span>
                  <span className="info-num">{item.rxInfo[0].name}</span>
                </div>
                <div className="text-left">
                  <span className="info-name">شماره شناسه:</span>
                  <span className="info-num">{packetID}</span>
                </div>
                <div className="text-left">
                  <span className="info-name">ولتاژ:</span>
                  <span className="info-num">{volt}</span>
                </div>
                <div className="text-left">
                  <span className="info-name">آمپر:</span>
                  <span className="info-num">{amper}</span>
                </div>
              </div>
              <button className="btn">مشاهده جزییات</button>
            </div>
          );
        } else {
          return <h5>متاسفانه موردی یافت نشد</h5>;
        }
      }
    });
  };
  renderError = () => {
    const notFound = this.state.filter;
    return <span> متاسفانه موردی برای {notFound} پیدا تشد</span>;
  };

  render() {
    console.log(this.state.info);
    return (
      <div className="gozareshat-bg p-3 col-lg-10">
        <div className="w-100 form-box">
          <h6>برای جستجوی کنتور مورد نظر شناسه کنتور را وارد کنید.</h6>
          <div>
            <form
              onSubmit={this.handleSubmit}
              className="form-inline my-2 my-lg-0 search-form"
            >
              <input
                className="form-control mr-sm-2 "
                value={this.state.filter}
                onChange={this.handleChange}
                // onKeyPress={this.handleKeyPress}
                type="number"
                placeholder="شماره شناسه کنتور..."
                aria-label="Search"
              />
              <button
                className="btn btn-outline-success my-2 my-sm-0"
                type="submit"
              >
                جستجو
              </button>
            </form>
          </div>
        </div>
        <div className="list-box mt-3">{this.renderList()}</div>
      </div>
    );
  }
}

export default Gozareshat;
