const INITIAL_STATE = {
    data:[]
}

export default function DevicesDataReducer(state = {
    data:[]
}, action) {
  switch (action.type) {
    case "FETCH_DEVICES_DATA":

        return {  data: [...state.data, action.payload.options] }

    default:
      return state;
  }
}
