import React, { Component } from "react";
import { Link } from "react-router-dom";

class Menu extends Component {
  state = {
    selectedTab: 1,
  };
  render() {
    return (
      <aside>
        <header>
          {" "}
          <div className="menu-tab mt-5">
            <ul className="rtl nav d-flex flex-column">
              <li
                className="nav-item"
                onClick={() => this.setState({ selectedTab: 1 })}
                style={{
                  backgroundColor:
                    this.state.selectedTab === 1 ? "#ffffff45" : "none",
                  borderRadius:
                    this.state.selectedTab === 1 ? "0px 35px 35px 0px" : "none",
                }}
              >
                <Link
                  className="nav-link active"
                  to="/"
                  style={{
                    color: this.state.selectedTab === 1 ? "#11999e" : "#ffffff45",
                  }}
                >
                  نمایش لحظه ای
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Gozareshat">
                  گزارشات
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Setting">
                  تنظیمات
                </Link>
              </li>
            </ul>
          </div>
        </header>
      </aside>
    );
  }
}

export default Menu;
