import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { fetchAssetDevices, setAssetDetail, fetchSingleAsset } from "../store/actions";
import building from "../img/building.svg";

class TowerDetail extends Component {
  componentDidMount() {
    this.props.fetchSingleAsset({
      assetId: this.props.id,
      type: this.props.type,
    });
    this.props.fetchAssetDevices({
      assetId: this.props.id,
      type: this.props.type,
    });
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  goBack= ()=>{
    this.props.history.push('/')
  }
  render() {
    console.log(this.props);
    return (
      <div className="card tower-info">
        <div className="card-body">
          <div>
            <img src={building} width="50px" alt="" />
          <div>
            <h5 className="card-title">نام: {this.props.assetInfo.name}</h5>
            <p className="card-text">نوع:{this.props.assetInfo.type}</p>
            <p className="card-text">تعداد کنتور :{this.props.assetInfo.type}</p>
          </div>
          </div>
          <a  onClick={this.goBack} className="btn btn-light mt-auto">
            بازگشت
          </a>
        </div>
      </div>
    );
  }
}




const mapStateToProps = ({ AssetDevicesReducer, SingleAssetReducer,AssetDetailReducer }) => {
  return {
    devices: AssetDevicesReducer.data,
    // assetInfo: AssetDetailReducer.data.asset,
    assetInfo: SingleAssetReducer.data,
    id: AssetDetailReducer.data.assetId,
    type: AssetDetailReducer.data.type,
  };
};

export default connect(mapStateToProps, {
  fetchAssetDevices,
  setAssetDetail,
  fetchSingleAsset
})(withRouter(TowerDetail));
