import React, { Component } from "react";
import { NavLink } from "react-router-dom";

// import clock from "../img/clock.svg";
// import calendar from "../img/calendar.svg";
// import setting from "../img/settings.svg";
// import clockYellow from "../img/clock-y.svg";
// import calendarYellow from "../img/calendar-y.svg";
// import settingYellow from "../img/settings-y.svg";
import logo from "../img/logo.svg";

class Menu extends Component {
  render() {
    return (
      <aside>
        <header>
          <div className="d-flex align-items-center flex-column mb-5">
            <img src={logo} alt="" width="70px" />
            <h6>سامانه هوشمند کنترل برق</h6>
          </div>
          <div className="menu-tab mt-5">
            <ul className="rtl nav d-flex flex-column">
              <li className="nav-item">
                <NavLink
                  className="nav-link "
                  exact
                  to="/"
                  activeStyle={{
                    color: "#ffdf42",
                  }}
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke-width="2"
                    stroke-linecap="round"
                    stroke-linejoin="round"
                  >
                    <circle cx="12" cy="12" r="10"></circle>
                    <polyline points="12 6 12 12 16 14"></polyline>
                  </svg>
                  نمایش لحظه ای
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink
                  className="nav-link"
                  to="/Gozareshat"
                  activeStyle={{
                    color: "#ffdf42",
                  }}
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="#ffdf42"
                    stroke-width="2"
                    stroke-linecap="round"
                    stroke-linejoin="round"
                  >
                    <rect
                      x="3"
                      y="4"
                      width="18"
                      height="18"
                      rx="2"
                      ry="2"
                    ></rect>
                    <line x1="16" y1="2" x2="16" y2="6"></line>
                    <line x1="8" y1="2" x2="8" y2="6"></line>
                    <line x1="3" y1="10" x2="21" y2="10"></line>
                  </svg>
                  گزارشات
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink
                  className="nav-link"
                  to="/Setting"
                  activeStyle={{
                    color: "#ffdf42",
                  }}
                >
                  تنظیمات
                </NavLink>
              </li>
            </ul>
          </div>
        </header>
      </aside>
    );
  }
}

export default Menu;
