import React, { Component } from "react";
import L from "leaflet";
import {
  fetchDevicesID,
  fetchDevicesData,
  fetchAllBuildings,
  fetchBuildingAssets,
} from "../store/actions";
import { connect } from "react-redux";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

var mapIcon = L.icon({
  iconUrl: require("../img/placeholder.svg"),
  iconRetinaUrl: require("../img/placeholder.svg"),
  iconSize: [50, 70],
  iconAnchor: [12.3, 41],
  popupAnchor: [11, -28],
});

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ID: [],
      info: [],
      lng: 53.982,
      lat: 26.5425,
      zoom: 13,
    };
  }
  _isMounted = false;

  componentDidMount() {
    this._isMounted = true;
    if (this._isMounted) {
      // console.log(this.props);
      this.props.fetchDevicesID();
      // this.props.fetchDevicesData(this.props.id);
      this.props.fetchAllBuildings();
      this.props.fetchBuildingAssets(this.props.uuid);
    }
  }

  componentWillUnmount() {
    this._isMounted = false;
  }
  renderMarker = () => {
    return this.props.assets.map((item, index) => {
      const position = [item.latitude, item.longitude];

      return (
        <Marker position={position} icon={mapIcon} key={index}>
          <Popup>
            <span className="pop-title">اطلاعات دستگاه</span>

            <div className="info-pop">
              <div className="text-left">
                <span className="info-name">نام مالک:</span>
                <span className="info-num">{item.name}</span>
              </div>
              <div className="text-left">
                <span className="info-name">شهر:</span>
                <span className="info-num">{item.country}</span>
              </div>
            </div>
          </Popup>
        </Marker>
      );
    });
  };
  onChangeValue = (event) => {
    console.log(event.target.value);
  };
  render() {
    console.log(this.props.assets);
    const position = [this.state.lat, this.state.lng];
    return (
      <div className="col-lg-10 p-0">
        <div className="selector">
          <div onChange={this.onChangeValue}>
            <div>
              <label htmlFor=""></label>
              <input type="radio" value="Male" name="gender" /> 
            </div>
            <input type="radio" value="Female" name="gender" /> Female
            <input type="radio" value="Other" name="gender" /> Other
          </div>
        </div>
        <Map center={position} zoom={this.state.zoom} className="h-100">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this.renderMarker()}
        </Map>
      </div>
    );
  }
}

const mapStateToProps = ({
  DevicesIDReducer,
  DevicesDataReducer,
  AllBuildingsReducer,
  BuildingAssetsReducer,
}) => {
  // console.log({AllBuildingsReducer});
  return {
    // id: state.ID.devicesID
    id: DevicesIDReducer.data,
    // info : DevicesDataReducer.data,
    uuid: AllBuildingsReducer.data[0].uuid,
    assets: BuildingAssetsReducer.data,
  };
};

export default connect(mapStateToProps, {
  fetchDevicesID,
  fetchBuildingAssets,
  fetchAllBuildings,
})(Home);
